INSERT INTO `es_article` (`article_id`, `user_id`, `cat_id`, `type`, `title`, `content`, `click_count`, `is_show`, `is_public`, `add_time`) VALUES
	(14, 18, 5, 1, '打开netbeans IDE报svn错误的问题 ', '&lt;p&gt;打开netbeans IDE时，如果发现弹框报错，关于SVN的，类似：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://blog.csdn.net/qdujunjie/article/details/17301847&quot; alt=&quot;&quot;/&gt;&lt;img src=&quot;http://blog.csdn.net/qdujunjie/article/details/17301847&quot; alt=&quot;&quot;/&gt;org.tigris.subversion.javahl.ClientException: The path &amp;#39;D:\\wamp\\www\\ThinkPHP&amp;#39; appears to be part of a Subversion 1.7 or greater&lt;br/&gt;working copy. &amp;nbsp;Please upgrade your Subversion client to use this&lt;br/&gt;working copy.&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://img.blog.csdn.net/20131213143600671?watermark/2/text/aHR0cDovL2Jsb2cuY3Nkbi5uZXQvcWR1anVuamll/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70/gravity/Center&quot; alt=&quot;&quot;/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;可以如下处理：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;到Netbeans目录：C:\\Program Files (x86)\\NetBeans 7.0\\etc&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;找到此文件：netbeans.conf&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;可以看到此文件有配置如下：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;# ${HOME} will be replaced by JVM user.home system property&lt;br/&gt;netbeans_default_userdir=&amp;quot;${HOME}/.netbeans/7.0&amp;quot;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;# Options used by NetBeans launcher by default, can be overridden by explicit&lt;br/&gt;# command line switches:&lt;br/&gt;netbeans_default_options=&amp;quot;-J-client -J-Xss2m -J-Xms32m \n-J-XX:PermSize=32m -J-XX:MaxPermSize=384m \n-J-Dapple.laf.useScreenMenuBar=true \n-J-Dapple.awt.graphics.UseQuartz=true -J-Dsun.java2d.noddraw=true&amp;quot;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;#-J-DsvnClientAdapterFactory=commandline&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;# Note that a default -Xmx is selected for you automatically.&lt;br/&gt;# You can find this value in var/log/messages.log file in your userdir.&lt;br/&gt;# The automatically selected value can be overridden by specifying -J-Xmx here&lt;br/&gt;# or on the command line.&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;# If you specify the heap size (-Xmx) explicitly, you may also want to enable&lt;br/&gt;# Concurrent Mark &amp;amp; Sweep garbage collector. In such case add the following&lt;br/&gt;# options to the netbeans_default_options:&lt;br/&gt;# -J-XX:+UseConcMarkSweepGC -J-XX:+CMSClassUnloadingEnabled -J-XX:+CMSPermGenSweepingEnabled&lt;br/&gt;# (see http://wiki.netbeans.org/FaqGCPauses)&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;# Default location of JDK, can be overridden by using --jdkhome &amp;lt;dir&amp;gt;:&lt;br/&gt;netbeans_jdkhome=&amp;quot;C:\\Program Files (x86)\\Java\\jdk1.6.0_22&amp;quot;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;# Additional module clusters, using ${path.separator} (&amp;#39;;&amp;#39; on Windows or &amp;#39;:&amp;#39; on Unix):&lt;br/&gt;#netbeans_extraclusters=&amp;quot;/absolute/path/to/cluster1:/absolute/path/to/cluster2&amp;quot;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;# If you have some problems with detect of proxy settings, you may want to enable&lt;br/&gt;# detect the proxy settings provided by JDK5 or higher.&lt;br/&gt;# In such case add -J-Djava.net.useSystemProxies=true to the netbeans_default_options.&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;看到其中的：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;# ${HOME} will be replaced by JVM user.home system property&lt;br/&gt;netbeans_default_userdir=&amp;quot;${HOME}/.netbeans/7.0&amp;quot;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;# Options used by NetBeans launcher by default, can be overridden by explicit&lt;br/&gt;# command line switches:&lt;br/&gt;netbeans_default_options=&amp;quot;-J-client -J-Xss2m -J-Xms32m \n-J-XX:PermSize=32m -J-XX:MaxPermSize=384m \n-J-Dapple.laf.useScreenMenuBar=true \n-J-Dapple.awt.graphics.UseQuartz=true -J-Dsun.java2d.noddraw=true&amp;quot;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;#-J-DsvnClientAdapterFactory=commandline&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;将&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;netbeans_default_options=&amp;quot;-J-client -J-Xss2m -J-Xms32m \n-J-XX:PermSize=32m -J-XX:MaxPermSize=384m \n-J-Dapple.laf.useScreenMenuBar=true \n-J-Dapple.awt.graphics.UseQuartz=true -J-Dsun.java2d.noddraw=true&amp;quot;&lt;/p&gt;&lt;p&gt;加入 -J-DsvnClientAdapterFactory=commandline，改为：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;netbeans_default_options=&amp;quot;-J-client -J-Xss2m -J-Xms32m \n-J-XX:PermSize=32m -J-XX:MaxPermSize=384m \n-J-Dapple.laf.useScreenMenuBar=true \n-J-Dapple.awt.graphics.UseQuartz=true -J-Dsun.java2d.noddraw=true \n-J-DsvnClientAdapterFactory=commandline&amp;quot;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;然后打开试试，是不是没有了？&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 0, 1, 1, 1444957871),
	(15, 18, 6, 1, 'CI,ThinkPHP框架 .htaccess 隐藏(去掉)url中index.php的方法', '&lt;p&gt;使用过单入口PHP框架的盆友应该都会碰到这个问题。那就是去除、隐藏入口文件index.php的问题。对于seo及url美化是和必要的。网上相关的文章很多，这里就不多说了。&lt;/p&gt;&lt;p&gt;本文主要对实现这个功能的apache rewrite语句的作用解析一下。希望能帮到大家。&lt;/p&gt;&lt;p&gt;ThinkPHP框架去除index.php官方解决方法：&lt;span style=&quot;text-decoration:underline;&quot;&gt;http://www.thinkphp.cn/code/25.html&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot; language-php&quot;&gt;RewriteEngine&amp;nbsp;on#&amp;nbsp;如果文件存在，就直接访问文件，不进行下面的RewriteRule.RewriteCond&amp;nbsp;%{REQUEST_FILENAME}&amp;nbsp;!-f#&amp;nbsp;如果目录存在，就直接访问目录，不进行下面的RewriteRule.RewriteCond&amp;nbsp;%{REQUEST_FILENAME}&amp;nbsp;!-d\nRewriteRule&amp;nbsp;^(.*)$&amp;nbsp;index.php/$1&amp;nbsp;[L,NC]&lt;/pre&gt;&lt;p&gt;需要的注意的问题。在处理此问题时，请务必确认apache的rewrite模块是否已经开启，使用 &lt;a href=&quot;http://www.hi-docs.com/php/phpinfo.html&quot; title=&quot;php phpinfo()函数用法，例子&quot; target=&quot;_blank&quot;&gt;phpinfo()&lt;/a&gt; 函数可以查看apache已开启的模块列表。&lt;/p&gt;&lt;p&gt;如果你使用的是IIS，或者nginx。请查找相关的文章，本文不适用于IIS，nginx环境。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 0, 1, 1, 1444974610),
	(16, 18, 7, 1, 'svn利用钩子实现代码同步到web目录', '&lt;p&gt;思路：&amp;nbsp;&amp;nbsp;&lt;br/&gt;找 到SVN \nServer中的仓库（Repositories）文件夹的位置，在相应的项目文件夹中找到hooks文件夹。在该文件夹中添加一个post- \ncommit文件；当有commit动作发生时（提交到SVN Server时）就会执行这个post-commit文件，在该文件实现数据的复制。&amp;nbsp;&amp;nbsp;&lt;br/&gt;&lt;br/&gt;实例：&amp;nbsp;&amp;nbsp;&lt;br/&gt;环境：SVN Server与测试用的Web Server在同一台Windows系统服务器上，SVN Server使用的是VisualSVN，Web Server使用的是Apache。项目的名称为test，客户端使用TortoiseSVN。&amp;nbsp;&amp;nbsp;&lt;br/&gt;首先在apache的www目录下创建一个文件夹，进行checkout，输入需要用户名和密码，将test项目取出到该文件夹中。&amp;nbsp;&amp;nbsp;&lt;br/&gt;找到SVN Server的Repositories目录，在Repositories/test/hooks文件夹中创建post-commit.bat，在其中写入命令：&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;quot;C:\\Program Files\\VisualSVN Server\\bin\\svn.exe&amp;quot; update &amp;quot;D:\\www\\test&amp;quot; --quiet --username name --password pwd&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;（即执行svn update命令，请自行替换用户名和密码）&amp;nbsp;&lt;/p&gt;&lt;p&gt;也可以这样写&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;cnblogs_code_copy&quot;&gt;&lt;a title=&quot;复制代码&quot;&gt;&lt;img src=&quot;http://common.cnblogs.com/images/copycode.gif&quot; alt=&quot;复制代码&quot;/&gt;&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre&gt;@echo&amp;nbsp;off\nSET&amp;nbsp;REPOS=%1\nSET&amp;nbsp;USER=%2SET&amp;nbsp;\nSVN=&amp;quot;E:/Program&amp;nbsp;Files/VisualSVN&amp;nbsp;Server/bin/svn.exe&amp;quot;\nSET&amp;nbsp;DIR=&amp;quot;E:/wamp2/www&amp;quot;&amp;nbsp;(call&amp;nbsp;%SVN%&amp;nbsp;update&amp;nbsp;%DIR%&amp;nbsp;--username&amp;nbsp;izaodao&amp;nbsp;--password&amp;nbsp;izaodao&amp;nbsp;--non-interactive)&lt;/pre&gt;&lt;p&gt;&lt;span class=&quot;cnblogs_code_copy&quot;&gt;&lt;a title=&quot;复制代码&quot;&gt;&lt;img src=&quot;http://common.cnblogs.com/images/copycode.gif&quot; alt=&quot;复制代码&quot;/&gt;&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://my.oschina.net/u/2129&quot; rel=&quot;nofollow&quot; target=&quot;_blank&quot;&gt;&amp;nbsp;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;大功告成。&amp;nbsp;&amp;nbsp;&lt;br/&gt;注：&lt;/p&gt;&lt;p&gt;1.web目录下一定要用TortoiseSVN&amp;nbsp;checkout出Repositories的代码&lt;/p&gt;&lt;p&gt;2.&lt;/p&gt;&lt;pre&gt;-username&amp;nbsp;izaodao&amp;nbsp;--password&amp;nbsp;izaodao&lt;/pre&gt;&lt;p&gt;izaodao替换为登录svn的用户名和密码&lt;/p&gt;&lt;p&gt;在使用中可能会遇到的错误排除 ：&lt;br/&gt;&lt;br/&gt;1.Error: svn: 解析“D:\\www\\test”出错，或svn: E020024: Error resolving case of &amp;#39;D:\\www\\test&amp;quot;&amp;#39;，则有可能是因为目标文件名之后多加了”\\”&lt;br/&gt;&lt;br/&gt;2.“D:\\www\\test&amp;quot;或Skipped&amp;nbsp;&amp;#39;d:\\www\\test&amp;#39;,则可能是因为没有执行步骤提交更新处理文件 即上面提到的.bat文件，svn.exe不认识目标文件夹&lt;br/&gt;&lt;br/&gt;3.&amp;nbsp;Working copy &amp;#39;D:\\www\\test&amp;#39; is too old，则可能需要使用sven.exe upgrade命令重新整理一下目标文件夹 或者重新安装一个较高客户端版本至少要2.6以上吧&lt;/p&gt;&lt;p&gt;4.还有一个注意问题——Visual SVN Server的权限，否则可能会出现下列错误：&lt;br/&gt;post-commit hook failed (exit code 1) with output:&lt;br/&gt;svn: E155004: Working copy &amp;#39;D:\\www\\Test&amp;#39; locked&lt;br/&gt;svn: E200031: sqlite: attempt to write a readonly database&lt;br/&gt;svn: E200031: sqlite: attempt to write a readonly database&lt;br/&gt;svn: run &amp;#39;svn cleanup&amp;#39; to remove locks (type &amp;#39;svn help cleanup&amp;#39; for details)&lt;br/&gt;原因是Visual SVN Server服务的执行权限不够，不能对指定目录做读写操作。解决办法，修改Visual SVN Server&amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;简单操作 win+R&amp;nbsp; 运行 services.msc 找到visual svn server 服务 右键属性 登陆面板 设置桌面交互或者管理员权限再试&lt;/p&gt;', 0, 1, 1, 1444981814),
	(17, 18, 3, 1, 'MySQL性能优化的最佳20+条经验', '&lt;p&gt;今天，数据库的操作越来越成为整个应用的性能瓶颈了，这点对于Web应用尤其明显。关于数据库的性能，这并不只是DBA才需要担心的事，而这更是我 \n们程序员需要去关注的事情。当我们去设计数据库表结构，对操作数据库时（尤其是查表时的SQL语句），我们都需要注意数据操作的性能。这里，我们不会讲过\n 多的SQL语句的优化，而只是针对MySQL这一Web应用最多的数据库。希望下面的这些优化技巧对你有用。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;1. 为查询缓存优化你的查询&lt;/strong&gt;&lt;br/&gt;大多数的MySQL服务器都开启了查询缓存。这是提高性最有效的方法之一，而且这是被MySQL的数据库引擎处理的。当有很多相同的查询被执行了多次的时候，这些查询结果会被放到一个缓存中，这样，后续的相同的查询就不用操作表而直接访问缓存结果了。&lt;br/&gt;&lt;br/&gt;这里最主要的问题是，对于程序员来说，这个事情是很容易被忽略的。因为，我们某些查询语句会让MySQL不使用缓存。请看下面的示例：&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;/code&gt;&lt;/p&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;查询缓存不开启&lt;/span&gt;&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;signup_date&amp;nbsp;&amp;gt;=&amp;nbsp;CURDATE()&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;开启查询缓存&lt;/span&gt;&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;span class=&quot;pln&quot;&gt; $today&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;date&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;Y-m-d&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;signup_date&amp;nbsp;&amp;gt;=&amp;nbsp;&amp;#39;$today&amp;#39;&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;复制代码&lt;/code&gt;&lt;/p&gt;&lt;p&gt;上\n面两条SQL语句的差别就是 CURDATE() ，MySQL的查询缓存对这个函数不起作用。所以，像 NOW() 和 RAND() \n或是其它的诸如此类的SQL函数都不会开启查询缓存，因为这些函数的返回是会不定的易变的。所以，你所需要的就是用一个变量来代替MySQL的函数，从而\n 开启缓存。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;2. EXPLAIN 你的 SELECT 查询&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;使用 EXPLAIN 关键字可以让你知道MySQL是如何处理你的SQL语句的。这可以帮你分析你的查询语句或是表结构的性能瓶颈。&lt;br/&gt;&lt;br/&gt;EXPLAIN 的查询结果还会告诉你你的索引主键被如何利用的，你的数据表是如何被搜索和排序的……等等，等等。&lt;br/&gt;&lt;br/&gt;挑一个你的SELECT语句（推荐挑选那个最复杂的，有多表联接的），把关键字EXPLAIN加到前面。你可以使用phpmyadmin来做这个事。然后，你会看到一张表格。下面的这个示例中，我们忘记加上了group_id索引，并且有表联接：&lt;br/&gt;&lt;a href=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f216f6e467f.jpg&quot; target=&quot;_blank&quot;&gt;&lt;img src=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f216f6e467f.jpg&quot;/&gt;&lt;/a&gt;&lt;br/&gt;当我们为 group_id 字段加上索引后：&lt;br/&gt;&lt;a href=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f21715a0838.jpg&quot; target=&quot;_blank&quot;&gt;&lt;img src=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f21715a0838.jpg&quot;/&gt;&lt;/a&gt;&lt;br/&gt;我们可以看到，前一个结果显示搜索了 7883 行，而后一个只是搜索了两个表的 9 和 16 行。查看rows列可以让我们找到潜在的性能问题。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;3. 当只要一行数据时使用 LIMIT 1&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;当你查询表的有些时候，你已经知道结果只会有一条结果，但因为你可能需要去fetch游标，或是你也许会去检查返回的记录数。&lt;br/&gt;&lt;br/&gt;在这种情况下，加上 LIMIT 1 可以增加性能。这样一样，MySQL数据库引擎会在找到一条数据后停止搜索，而不是继续往后查少下一条符合记录的数据。&lt;br/&gt;&lt;br/&gt;下面的示例，只是为了找一下是否有“中国”的用户，很明显，后面的会比前面的更有效率。（请注意，第一条中是Select *，第二条是Select 1）&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;没有效率的：&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;country&amp;nbsp;=&amp;nbsp;&amp;#39;China&amp;#39;&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;if&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;mysql_num_rows&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$r&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;0&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;{&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;...&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;}&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;有效率的：&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;1&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;country&amp;nbsp;=&amp;nbsp;&amp;#39;China&amp;#39;&amp;nbsp;LIMIT&amp;nbsp;1&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;if&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;mysql_num_rows&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$r&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;0&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;{&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;...&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;}&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;4. 为搜索字段建索引&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;索引并不一定就是给主键或是唯一的字段。如果在你的表中，有某个字段你总要会经常用来做搜索，那么，请为其建立索引吧。&lt;br/&gt;&lt;a href=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f2174d245c3.jpg&quot; target=&quot;_blank&quot;&gt;&lt;img src=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f2174d245c3.jpg&quot;/&gt;&lt;/a&gt;&lt;br/&gt;从上图你可以看到那个搜索字串 “last_name LIKE ‘a%’”，一个是建了索引，一个是没有索引，性能差了4倍左右。&lt;br/&gt;&lt;br/&gt;另外，你应该也需要知道什么样的搜索是不能使用正常的索引的。例如，当你需要在一篇大的文章中搜索一个词时，如： “WHERE \npost_content LIKE ‘%apple%’”，索引可能是没有意义的。你可能需要使用MySQL全文索引 \n或是自己做一个索引（比如说：搜索关键词或是Tag什么的）&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;5. 在Join表的时候使用相当类型的例，并将其索引&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;如果你的应用程序有很多 JOIN 查询，你应该确认两个表中Join的字段是被建过索引的。这样，MySQL内部会启动为你优化Join的SQL语句的机制。&lt;br/&gt;&lt;br/&gt;而且，这些被用来Join的字段，应该是相同的类型的。例如：如果你要把 DECIMAL 字段和一个 INT 字段Join在一起，MySQL就无法使用它们的索引。对于那些STRING类型，还需要有相同的字符集才行。（两个表的字符集有可能不一样）&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;在state中查找company&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;company_name&amp;nbsp;FROM&amp;nbsp;users&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;str&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;LEFT&amp;nbsp;JOIN&amp;nbsp;companies&amp;nbsp;ON&amp;nbsp;(users.state&amp;nbsp;=&amp;nbsp;companies.state)&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;str&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;WHERE&amp;nbsp;users.id&amp;nbsp;=&amp;nbsp;$user_id&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;两个&amp;nbsp;state&amp;nbsp;字段应该是被建过索引的，而且应该是相当的类型，相同的字符集。&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;6. 千万不要 ORDER BY RAND()&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;想打乱返回的数据行？随机挑一个数据？真不知道谁发明了这种用法，但很多新手很喜欢这样用。但你确不了解这样做有多么可怕的性能问题。&lt;br/&gt;&lt;br/&gt;如果你真的想把返回的数据行打乱了，你有N种方法可以达到这个目的。这样使用只让你的数据库的性能呈指数级的下降。这里的问题是：MySQL会不得 \n不去执行RAND()函数（很耗CPU时间），而且这是为了每一行记录去记行，然后再对其排序。就算是你用了Limit 1也无济于事（因为要排序）&lt;br/&gt;&lt;br/&gt;下面的示例是随机挑一条记录&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;千万不要这样做：&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;ORDER&amp;nbsp;BY&amp;nbsp;RAND()&amp;nbsp;LIMIT&amp;nbsp;1&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;这要会更好：&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;count(*)&amp;nbsp;FROM&amp;nbsp;user&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $d&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_fetch_row&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$r&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $rand&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mt_rand&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;0&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$d&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;[&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;0&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;]&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;-&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;1&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;LIMIT&amp;nbsp;$rand,&amp;nbsp;1&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;7. 避免 SELECT *&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;从数据库里读出越多的数据，那么查询就会变得越慢。并且，如果你的数据库服务器和WEB服务器是两台独立的服务器的话，这还会增加网络传输的负载。&lt;br/&gt;&lt;br/&gt;所以，你应该养成一个需要什么就取什么的好的习惯。&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;不推荐&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;user_id&amp;nbsp;=&amp;nbsp;1&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $d&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_fetch_assoc&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$r&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; echo&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;Welcome&amp;nbsp;{$d[&amp;#39;username&amp;#39;]}&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;推荐&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;user_id&amp;nbsp;=&amp;nbsp;1&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; $d&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_fetch_assoc&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$r&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; echo&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;Welcome&amp;nbsp;{$d[&amp;#39;username&amp;#39;]}&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;8. 永远为每张表设置一个ID&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;我们应该为数据库里的每张表都设置一个ID做为其主键，而且最好的是一个INT型的（推荐使用UNSIGNED），并设置上自动增加的AUTO_INCREMENT标志。&lt;br/&gt;&lt;br/&gt;就算是你 users 表有一个主键叫 “email”的字段，你也别让它成为主键。使用 VARCHAR 类型来当主键会使用得性能下降。另外，在你的程序中，你应该使用表的ID来构造你的数据结构。&lt;br/&gt;&lt;br/&gt;而且，在MySQL数据引擎下，还有一些操作需要使用主键，在这些情况下，主键的性能和设置变得非常重要，比如，集群，分区……&lt;br/&gt;&lt;br/&gt;在这里，只有一个情况是例外，那就是“关联表”的“外键”，也就是说，这个表的主键，通过若干个别的表的主键构成。我们把这个情况叫做“外键”。比 \n如：有一个“学生表”有学生的ID，有一个“课程表”有课程ID，那么，“成绩表”就是“关联表”了，其关联了学生表和课程表，在成绩表中，学生ID和课\n 程ID叫“外键”其共同组成主键。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;9. 使用 ENUM 而不是 VARCHAR&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;ENUM 类型是非常快和紧凑的。在实际上，其保存的是 TINYINT，但其外表上显示为字符串。这样一来，用这个字段来做一些选项列表变得相当的完美。&lt;br/&gt;&lt;br/&gt;如果你有一个字段，比如“性别”，“国家”，“民族”，“状态”或“部门”，你知道这些字段的取值是有限而且固定的，那么，你应该使用 ENUM 而不是 VARCHAR。&lt;br/&gt;&lt;br/&gt;MySQL也有一个“建议”（见第十条）告诉你怎么去重新组织你的表结构。当你有一个 VARCHAR 字段时，这个建议会告诉你把其改成 ENUM 类型。使用 PROCEDURE ANALYSE() 你可以得到相关的建议。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;10. 从 PROCEDURE ANALYSE() 取得建议&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;PROCEDURE ANALYSE() 会让 MySQL 帮你去分析你的字段和其实际的数据，并会给你一些有用的建议。只有表中有实际的数据，这些建议才会变得有用，因为要做一些大的决定是需要有数据作为基础的。&lt;br/&gt;&lt;br/&gt;例如，如果你创建了一个 INT 字段作为你的主键，然而并没有太多的数据，那么，PROCEDURE ANALYSE()会建议你把这个字段的类型改成\n MEDIUMINT 。或是你使用了一个 VARCHAR 字段，因为数据不多，你可能会得到一个让你把它改成 ENUM \n的建议。这些建议，都是可能因为数据不够多，所以决策做得就不够准。&lt;br/&gt;&lt;br/&gt;在phpmyadmin里，你可以在查看表时，点击 “Propose table structure” 来查看这些建议&lt;br/&gt;&lt;a href=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f217becf0a8.jpg&quot; target=&quot;_blank&quot;&gt;&lt;img src=&quot;http://www.thinkphp.cn/Uploads/editor/2013-07-26/51f217becf0a8.jpg&quot;/&gt;&lt;/a&gt;&lt;br/&gt;一定要注意，这些只是建议，只有当你的表里的数据越来越多时，这些建议才会变得准确。一定要记住，你才是最终做决定的人。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;11. 尽可能的使用 NOT NULL&lt;/strong&gt;&lt;br/&gt;除非你有一个很特别的原因去使用 NULL 值，你应该总是让你的字段保持 NOT NULL。这看起来好像有点争议，请往下看。&lt;br/&gt;&lt;br/&gt;首先，问问你自己“Empty”和“NULL”有多大的区别（如果是INT，那就是0和NULL）？如果你觉得它们之间没有什么区别，那么你就不要使用NULL。（你知道吗？在 Oracle 里，NULL 和 Empty 的字符串是一样的！)&lt;br/&gt;&lt;br/&gt;不要以为 NULL 不需要空间，其需要额外的空间，并且，在你进行比较的时候，你的程序会更复杂。 当然，这里并不是说你就不能使用NULL了，现实情况是很复杂的，依然会有些情况下，你需要使用NULL值。&lt;br/&gt;&lt;br/&gt;下面摘自MySQL自己的文档：&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pun&quot;&gt;“&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;NULL&amp;nbsp;columns&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;require&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;additional&amp;nbsp;space&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;in&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;the&amp;nbsp;row&amp;nbsp;to&amp;nbsp;record&amp;nbsp;whether&amp;nbsp;their&amp;nbsp;values&amp;nbsp;are&amp;nbsp;NULL&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;.&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;typ&quot;&gt;For&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;typ&quot;&gt;MyISAM&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;tables&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;each&amp;nbsp;NULL&amp;nbsp;column&amp;nbsp;takes&amp;nbsp;one&amp;nbsp;bit&amp;nbsp;extra&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;rounded&amp;nbsp;up&amp;nbsp;to&amp;nbsp;the&amp;nbsp;nearest&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;byte&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;.”&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;12. Prepared Statements&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;Prepared Statements很像存储过程，是一种运行在后台的SQL语句集合，我们可以从使用 prepared statements 获得很多好处，无论是性能问题还是安全问题。&lt;br/&gt;&lt;br/&gt;Prepared Statements \n可以检查一些你绑定好的变量，这样可以保护你的程序不会受到“SQL注入式”攻击。当然，你也可以手动地检查你的这些变量，然而，手动的检查容易出问题，\n 而且很经常会被程序员忘了。当我们使用一些framework或是ORM的时候，这样的问题会好一些。&lt;br/&gt;&lt;br/&gt;在性能方面，当一个相同的查询被使用多次的时候，这会为你带来可观的性能优势。你可以给这些Prepared Statements定义一些参数，而MySQL只会解析一次。&lt;br/&gt;&lt;br/&gt;虽然最新版本的MySQL在传输Prepared Statements是使用二进制形势，所以这会使得网络传输非常有效率。&lt;br/&gt;&lt;br/&gt;当然，也有一些情况下，我们需要避免使用Prepared Statements，因为其不支持查询缓存。但据说版本5.1后支持了。&lt;br/&gt;&lt;br/&gt;在PHP中要使用prepared statements，你可以查看其使用手册：mysqli 扩展 或是使用数据库抽象层，如： PDO.&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;创建&amp;nbsp;prepared&amp;nbsp;statement&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;if&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$stmt&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;$mysqli&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;prepare&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;state=?&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;))&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;{&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;绑定参数&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;bind_param&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;s&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;$state&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;执行&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;execute&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;();&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;绑定结果&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;bind_result&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;$username&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;移动游标&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;fetch&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;();&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;printf&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;%s&amp;nbsp;is&amp;nbsp;from&amp;nbsp;%s\\n&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;$username&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;$state&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;-&amp;gt;&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;close&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;();&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;}&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;13. 无缓冲的查询&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;正常的情况下，当你在当你在你的脚本中执行一个SQL语句的时候，你的程序会停在那里直到没这个SQL语句返回，然后你的程序再往下继续执行。你可以使用无缓冲查询来改变这个行为。&lt;br/&gt;&lt;br/&gt;关于这个事情，在PHP的文档中有一个非常不错的说明： mysql_unbuffered_query() 函数：&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pun&quot;&gt;“&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;mysql_unbuffered_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;()&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;sends&amp;nbsp;the&amp;nbsp;SQL&amp;nbsp;query&amp;nbsp;query&amp;nbsp;to&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;typ&quot;&gt;MySQL&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;without&amp;nbsp;automatically&amp;nbsp;fetching&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;and&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;buffering&amp;nbsp;the&amp;nbsp;result&amp;nbsp;rows&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;as&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;()&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;does&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;.&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;typ&quot;&gt;This&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;saves&amp;nbsp;a&amp;nbsp;considerable&amp;nbsp;amount&amp;nbsp;of&amp;nbsp;memory&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;with&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;SQL&amp;nbsp;queries&amp;nbsp;that&amp;nbsp;produce&amp;nbsp;large&amp;nbsp;result&amp;nbsp;sets&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;,&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;and&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;you&amp;nbsp;can&amp;nbsp;start&amp;nbsp;working&amp;nbsp;on&amp;nbsp;the&amp;nbsp;result&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;set&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;immediately&amp;nbsp;after&amp;nbsp;the&amp;nbsp;first&amp;nbsp;row&amp;nbsp;has&amp;nbsp;been&amp;nbsp;retrieved&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;as&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;you&amp;nbsp;don&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;’&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;t&amp;nbsp;have&amp;nbsp;to&amp;nbsp;wait&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;until&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;the&amp;nbsp;complete&amp;nbsp;SQL&amp;nbsp;query&amp;nbsp;has&amp;nbsp;been&amp;nbsp;performed&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;.”&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;上\n面那句话翻译过来是说，mysql_unbuffered_query() \n发送一个SQL语句到MySQL而并不像mysql_query()一样去自动fethch和缓存结果。这会相当节约很多可观的内存，尤其是那些会产生大\n 量结果的查询语句，并且，你不需要等到所有的结果都返回，只需要第一行数据返回的时候，你就可以开始马上开始工作于查询结果了。&lt;br/&gt;&lt;br/&gt;然而，这会有一些限制。因为你要么把所有行都读走，或是你要在进行下一次的查询前调用 mysql_free_result() 清除结果。而且， \nmysql_num_rows() 或 mysql_data_seek() 将无法使用。所以，是否使用无缓冲的查询你需要仔细考虑。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;14. 把IP地址存成 UNSIGNED INT&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;很多程序员都会创建一个 VARCHAR(15) \n字段来存放字符串形式的IP而不是整形的IP。如果你用整形来存放，只需要4个字节，并且你可以有定长的字段。而且，这会为你带来查询上的优势，尤其是当\n 你需要使用这样的WHERE条件：IP between ip1 and ip2。&lt;br/&gt;&lt;br/&gt;我们必需要使用UNSIGNED INT，因为 IP地址会使用整个32位的无符号整形。&lt;br/&gt;&lt;br/&gt;而你的查询，你可以使用 INET_ATON() 来把一个字符串IP转成一个整形，并使用 INET_NTOA() 把一个整形转成一个字符串IP。在PHP中，也有这样的函数 ip2long() 和 long2ip()。&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt;$r&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;UPDATE&amp;nbsp;users&amp;nbsp;SET&amp;nbsp;ip&amp;nbsp;=&amp;nbsp;INET_ATON(&amp;#39;{$_SERVER[&amp;#39;REMOTE_ADDR&amp;#39;]}&amp;#39;)&amp;nbsp;WHERE&amp;nbsp;user_id&amp;nbsp;=&amp;nbsp;$user_id&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;15. 固定长度的表会更快&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;如果表中的所有字段都是“固定长度”的，整个表会被认为是 “static” 或 “fixed-length”。 例如，表中没有如下类型的字段： \nVARCHAR，TEXT，BLOB。只要你包括了其中一个这些字段，那么这个表就不是“固定长度静态表”了，这样，MySQL \n引擎会用另一种方法来处理。&lt;br/&gt;&lt;br/&gt;固定长度的表会提高性能，因为MySQL搜寻得会更快一些，因为这些固定的长度是很容易计算下一个数据的偏移量的，所以读取的自然也会很快。而如果字段不是定长的，那么，每一次要找下一条的话，需要程序找到主键。&lt;br/&gt;&lt;br/&gt;并且，固定长度的表也更容易被缓存和重建。不过，唯一的副作用是，固定长度的字段会浪费一些空间，因为定长的字段无论你用不用，他都是要分配那么多的空间。&lt;br/&gt;&lt;br/&gt;使用“垂直分割”技术（见下一条），你可以分割你的表成为两个一个是定长的，一个则是不定长的。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;16. 垂直分割&lt;/strong&gt;&lt;br/&gt;“垂直分割”是一种把数据库中的表按列变成几张表的方法，这样可以降低表的复杂度和字段的数目，从而达到优化的目的。（以前，在银行做过项目，见过一张表有100多个字段，很恐怖）&lt;br/&gt;&lt;br/&gt;示例一：在Users表中有一个字段是家庭地址，这个字段是可选字段，相比起，而且你在数据库操作的时候除了个 \n人信息外，你并不需要经常读取或是改写这个字段。那么，为什么不把他放到另外一张表中呢？ \n这样会让你的表有更好的性能，大家想想是不是，大量的时候，我对于用户表来说，只有用户ID，用户名，口令，用户角色等会被经常使用。小一点的表总是会有\n 好的性能。&lt;br/&gt;&lt;br/&gt;示例二： 你有一个叫 “last_login” \n的字段，它会在每次用户登录时被更新。但是，每次更新时会导致该表的查询缓存被清空。所以，你可以把这个字段放到另一个表中，这样就不会影响你对用户\nID，用户名，用户角色的不停地读取了，因为查询缓存会帮你增加很多性能。&lt;br/&gt;&lt;br/&gt;另外，你需要注意的是，这些被分出去的字段所形成的表，你不会经常性地去Join他们，不然的话，这样的性能会比不分割时还要差，而且，会是极数级的下降。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;17. 拆分大的 DELETE 或 INSERT 语句&lt;/strong&gt;&lt;br/&gt;如果你需要在一个在线的网站上去执行一个大的 DELETE 或 INSERT 查询，你需要非常小心，要避免你的操作让你的整个网站停止相应。因为这两个操作是会锁表的，表一锁住了，别的操作都进不来了。&lt;br/&gt;&lt;br/&gt;Apache 会有很多的子进程或线程。所以，其工作起来相当有效率，而我们的服务器也不希望有太多的子进程，线程和数据库链接，这是极大的占服务器资源的事情，尤其是内存。&lt;br/&gt;&lt;br/&gt;如果你把你的表锁上一段时间，比如30秒钟，那么对于一个有很高访问量的站点来说，这30秒所积累的访问进程/线程，数据库链接，打开的文件数，可能不仅仅会让你泊WEB服务Crash，还可能会让你的整台服务器马上掛了。&lt;br/&gt;&lt;br/&gt;所以，如果你有一个大的处理，你定你一定把其拆分，使用 LIMIT 条件是一个好的方法。下面是一个示例：&lt;code style=&quot;&quot; class=&quot;prettyprint linenums lang-php prettyprinted&quot;&gt;&lt;ol class=&quot;linenums list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;kwd&quot;&gt;while&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;1&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;{&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//每次只做1000条&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;mysql_query&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;str&quot;&gt;&amp;quot;DELETE&amp;nbsp;FROM&amp;nbsp;logs&amp;nbsp;WHERE&amp;nbsp;log_date&amp;nbsp;&amp;lt;=&amp;nbsp;&amp;#39;2009-11-01&amp;#39;&amp;nbsp;LIMIT&amp;nbsp;1000&amp;quot;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;if&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;mysql_affected_rows&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;()&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;==&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;0&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;pln&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;{&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;没得可删了，退出！&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;kwd&quot;&gt;break&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;}&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;com&quot;&gt;//&amp;nbsp;每次都要休息一会儿&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;usleep&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;lit&quot;&gt;50000&lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;);&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;pln&quot;&gt; &lt;/span&gt;&lt;span class=&quot;pun&quot;&gt;}&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;复制代码&lt;/p&gt;&lt;/code&gt;&lt;strong&gt;18. 越小的列会越快&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;对于大多数的数据库引擎来说，硬盘操作可能是最重大的瓶颈。所以，把你的数据变得紧凑会对这种情况非常有帮助，因为这减少了对硬盘的访问。&lt;br/&gt;&lt;br/&gt;参看 MySQL 的文档 Storage Requirements 查看所有的数据类型。&lt;br/&gt;&lt;br/&gt;如果一个表只会有几列罢了（比如说字典表，配置表），那么，我们就没有理由使用 INT 来做主键，使用 MEDIUMINT, SMALLINT 或是更小的 TINYINT 会更经济一些。如果你不需要记录时间，使用 DATE 要比 DATETIME 好得多。&lt;br/&gt;&lt;br/&gt;当然，你也需要留够足够的扩展空间，不然，你日后来干这个事，你会死的很难看，参看Slashdot的例子（2009年11月06日），一个简单的ALTER TABLE语句花了3个多小时，因为里面有一千六百万条数据。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;19. 选择正确的存储引擎&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;在 MySQL 中有两个存储引擎 MyISAM 和 InnoDB，每个引擎都有利有弊。酷壳以前文章《MySQL: InnoDB 还是 MyISAM?》讨论和这个事情。&lt;br/&gt;&lt;br/&gt;MyISAM \n适合于一些需要大量查询的应用，但其对于有大量写操作并不是很好。甚至你只是需要update一个字段，整个表都会被锁起来，而别的进程，就算是读进程都\n 无法操作直到读操作完成。另外，MyISAM 对于 SELECT COUNT(*) 这类的计算是超快无比的。&lt;br/&gt;&lt;br/&gt;InnoDB 的趋势会是一个非常复杂的存储引擎，对于一些小的应用，它会比 MyISAM 还慢。他是它支持“行锁” ，于是在写操作比较多的时候，会更优秀。并且，他还支持更多的高级应用，比如：事务。&lt;br/&gt;&lt;br/&gt;下面是MySQL的手册&lt;br/&gt;target=”_blank”MyISAM Storage Engine&lt;br/&gt;InnoDB Storage Engine&lt;br/&gt;&lt;strong&gt;20. 使用一个对象关系映射器（Object Relational Mapper）&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;使用 ORM (Object Relational Mapper)，你能够获得可靠的性能增涨。一个ORM可以做的所有事情，也能被手动的编写出来。但是，这需要一个高级专家。&lt;br/&gt;&lt;br/&gt;ORM 的最重要的是“Lazy Loading”，也就是说，只有在需要的去取值的时候才会去真正的去做。但你也需要小心这种机制的副作用，因为这很有可能会因为要去创建很多很多小的查询反而会降低性能。&lt;br/&gt;&lt;br/&gt;ORM 还可以把你的SQL语句打包成一个事务，这会比单独执行他们快得多得多。&lt;br/&gt;&lt;br/&gt;目前，个人最喜欢的PHP的ORM是：Doctrine。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;21. 小心“永久链接”&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;“永久链接”的目的是用来减少重新创建MySQL链接的次数。当一个链接被创建了，它会永远处在连接的状态，就算是数据库操作已经结束了。而且，自 \n从我们的Apache开始重用它的子进程后——也就是说，下一次的HTTP请求会重用Apache的子进程，并重用相同的 MySQL 链接。&lt;br/&gt;PHP手册：mysql_pconnect() &lt;br/&gt;&lt;br/&gt;在理论上来说，这听起来非常的不错。但是从个人经验（也是大多数人的）上来说，这个功能制造出来的麻烦事更多。因为，你只有有限的链接数，内存问题，文件句柄数，等等。&lt;br/&gt;&lt;br/&gt;而且，Apache 运行在极端并行的环境中，会创建很多很多的了进程。这就是为什么这种“永久链接”的机制工作地不好的原因。在你决定要使用“永久链接”之前，你需要好好地考虑一下你的整个系统的架构。&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;转载自：&lt;a title=&quot;http://www.thinkphp.cn/topic/3855.html&quot; target=&quot;_blank&quot; href=&quot;http://www.thinkphp.cn/topic/3855.html&quot;&gt;http://www.thinkphp.cn/topic/3855.html&lt;/a&gt;（ThinkPHP官网）&lt;/p&gt;', 0, 1, 1, 1444988712),
	(18, 18, 8, 1, 'JSONP跨域的原理解析', '&lt;p&gt;JavaScript是一种在Web开发中经常使用的前端动态脚本技术。在JavaScript中，有一个很重要的安全性限制，被称为“Same-\n Origin \nPolicy”（同源策略）。这一策略对于JavaScript代码能够访问的页面内容做了很重要的限制，即JavaScript只能访问与包含它的文档\n 在同一域下的内容。&lt;/p&gt;&lt;p&gt;　　JavaScript这个安全策略在进行多iframe或多窗口编程、以及Ajax编程时显得尤为重要。根据这个\n策略，在baidu.com下的 \n页面中包含的JavaScript代码，不能访问在google.com域名下的页面内容；甚至不同的子域名之间的页面也不能通过JavaScript代\n 码互相访问。对于Ajax的影响在于，通过XMLHttpRequest实现的Ajax请求，不能向不同的域提交请求，例如，在 \nabc.example.com下的页面，不能向def.example.com提交Ajax请求，等等。&lt;/p&gt;&lt;p&gt;　　然而，当进行一些比较深入的前端编程的时候，不可避免地需要进行跨域操作，这时候“同源策略”就显得过于苛刻。JSONP跨域GET请求是一个常用的解决方案，下面我们来看一下JSONP跨域是如何实现的，并且探讨下JSONP跨域的原理。&lt;/p&gt;&lt;p&gt;　　利用在页面中创建&amp;lt;script&amp;gt;节点的方法向不同域提交HTTP请求的方法称为JSONP，这项技术可以解决跨域提交Ajax请求的问题。JSONP的工作原理如下所述：&lt;/p&gt;&lt;p&gt;　\n　假设在http://example1.com/index.php这个页面中向http://example2.com \n/getinfo.php提交GET请求，我们可以将下面的JavaScript代码放在http://example1.com/index.php这\n 个页面中来实现：&lt;/p&gt;&lt;p&gt;Js代码&lt;/p&gt;&lt;ol class=&quot;dp-c list-paddingleft-2&quot; start=&quot;1&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;keyword&quot;&gt;var&lt;/span&gt;&amp;nbsp;eleScript=&amp;nbsp;document.createElement(&lt;span class=&quot;string&quot;&gt;&amp;quot;script&amp;quot;&lt;/span&gt;);&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;eleScript.type&amp;nbsp;=&amp;nbsp;&lt;span class=&quot;string&quot;&gt;&amp;quot;text/javascript&amp;quot;&lt;/span&gt;;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;eleScript.src&amp;nbsp;=&amp;nbsp;&lt;span class=&quot;string&quot;&gt;&amp;quot;http://example2.com/getinfo.php&amp;quot;&lt;/span&gt;;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;document.getElementsByTagName(&lt;span class=&quot;string&quot;&gt;&amp;quot;HEAD&amp;quot;&lt;/span&gt;)[0].appendChild(eleScript);&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;　　当GET请求从http://example2.com/getinfo.php返回时，可以返回一段JavaScript代码，这段代码会自动执行，可以用来负责调用http://example1.com/index.php页面中的一个callback函数。&lt;/p&gt;&lt;p&gt;　\n　JSONP的优点是：它不像XMLHttpRequest对象实现的Ajax请求那样受到同源策略的限制；它的兼容性更好，在更加古老的浏览器中 \n都可以运行，不需要XMLHttpRequest或ActiveX的支持；并且在请求完毕后可以通过调用callback的方式回传结果。&lt;/p&gt;&lt;p&gt;　　JSONP的缺点则是：它只支持GET请求而不支持POST等其它类型的HTTP请求；它只支持跨域HTTP请求这种情况，不能解决不同域的两个页面之间如何进行JavaScript调用的问题。&lt;/p&gt;&lt;p&gt;　　再来一个例子：&lt;/p&gt;&lt;p&gt;Js代码&lt;/p&gt;&lt;ol class=&quot;dp-c list-paddingleft-2&quot; start=&quot;1&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;keyword&quot;&gt;var&lt;/span&gt;&amp;nbsp;qsData&amp;nbsp;=&amp;nbsp;{&lt;span class=&quot;string&quot;&gt;&amp;#39;searchWord&amp;#39;&lt;/span&gt;:$(&lt;span class=&quot;string&quot;&gt;&amp;quot;#searchWord&amp;quot;&lt;/span&gt;).attr(&lt;span class=&quot;string&quot;&gt;&amp;quot;value&amp;quot;&lt;/span&gt;),&lt;span class=&quot;string&quot;&gt;&amp;#39;currentUserId&amp;#39;&lt;/span&gt;:&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;$(&lt;span class=&quot;string&quot;&gt;&amp;quot;#currentUserId&amp;quot;&lt;/span&gt;).attr(&lt;span class=&quot;string&quot;&gt;&amp;quot;value&amp;quot;&lt;/span&gt;),&lt;span class=&quot;string&quot;&gt;&amp;#39;conditionBean.pageSize&amp;#39;&lt;/span&gt;:$(&lt;span class=&quot;string&quot;&gt;&amp;quot;#pageSize&amp;quot;&lt;/span&gt;).attr(&lt;span class=&quot;string&quot;&gt;&amp;quot;value&amp;quot;&lt;/span&gt;)};&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;$.ajax({&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;async:&lt;span class=&quot;keyword&quot;&gt;false&lt;/span&gt;,&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;url:&amp;nbsp;http:&lt;span class=&quot;comment&quot;&gt;//跨域的dns/document!searchJSONResult.action,&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;type:&amp;nbsp;&lt;span class=&quot;string&quot;&gt;&amp;quot;GET&amp;quot;&lt;/span&gt;,&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;dataType:&amp;nbsp;&lt;span class=&quot;string&quot;&gt;&amp;#39;jsonp&amp;#39;&lt;/span&gt;,&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonp:&amp;nbsp;&lt;span class=&quot;string&quot;&gt;&amp;#39;jsoncallback&amp;#39;&lt;/span&gt;,&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;data:&amp;nbsp;qsData,&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;timeout:&amp;nbsp;5000,&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;beforeSend:&amp;nbsp;&lt;span class=&quot;keyword&quot;&gt;function&lt;/span&gt;(){&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;comment&quot;&gt;//jsonp&amp;nbsp;方式此方法不被触发.原因可能是dataType如果指定为jsonp的话,就已经不是ajax事件了&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;},&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;success:&amp;nbsp;&lt;span class=&quot;keyword&quot;&gt;function&lt;/span&gt;&amp;nbsp;(json)&amp;nbsp;{&lt;span class=&quot;comment&quot;&gt;//客户端jquery预先定义好的callback函数,成功获取跨域服务器上的json数据后,会动态执行这个callback函数&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;keyword&quot;&gt;if&lt;/span&gt;(json.actionErrors.length!=0){&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;alert(json.actionErrors);&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;genDynamicContent(qsData,type,json);&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;},&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;complete:&amp;nbsp;&lt;span class=&quot;keyword&quot;&gt;function&lt;/span&gt;(XMLHttpRequest,&amp;nbsp;textStatus){&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$.unblockUI({&amp;nbsp;fadeOut:&amp;nbsp;10&amp;nbsp;});&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;},&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;error:&amp;nbsp;&lt;span class=&quot;keyword&quot;&gt;function&lt;/span&gt;(xhr){&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;comment&quot;&gt;//jsonp&amp;nbsp;方式此方法不被触发.原因可能是dataType如果指定为jsonp的话,就已经不是ajax事件了&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;comment&quot;&gt;//请求出错处理&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;alert(&lt;span class=&quot;string&quot;&gt;&amp;quot;请求出错(请检查相关度网络状况.)&amp;quot;&lt;/span&gt;);&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;});&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;　　有时也会看到这样的写法：&lt;/p&gt;&lt;p&gt;Js代码&lt;/p&gt;&lt;ol class=&quot;dp-c list-paddingleft-2&quot; start=&quot;1&quot;&gt;&lt;li&gt;&lt;p&gt;$.getJSON(&lt;span class=&quot;string&quot;&gt;&amp;quot;http://跨域的dns/document!searchJSONResult.action?name1=&amp;quot;&lt;/span&gt;+value1+&lt;span class=&quot;string&quot;&gt;&amp;quot;&amp;amp;jsoncallback=?&amp;quot;&lt;/span&gt;,&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;keyword&quot;&gt;function&lt;/span&gt;(json){&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;keyword&quot;&gt;if&lt;/span&gt;(json.属性名==值){&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;comment&quot;&gt;//&amp;nbsp;执行代码&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;});&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;　　这种方式其实是上例$.ajax({..}) api的一种高级封装，有些$.ajax api底层的参数就被封装而不可见了。&lt;/p&gt;&lt;p&gt;　　这样，jquery就会拼装成如下的url get请求：&lt;/p&gt;&lt;p&gt;Js代码&lt;/p&gt;&lt;ol class=&quot;dp-c list-paddingleft-2&quot; start=&quot;1&quot;&gt;&lt;li&gt;&lt;p&gt;http:&lt;span class=&quot;comment&quot;&gt;//\n跨域的\ndns/document!searchJSONResult.action?&amp;amp;jsoncallback=jsonp1236827957501&amp;amp;_=1236828192549&amp;amp;searchWord=%E7%94%A8%E4%BE%8B¤tUserId=5351&amp;amp;conditionBean.pageSize=15&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;　\n　在响应端(http://跨域的dns/document!searchJSONResult.action)，通过 jsoncallback = \nrequest.getParameter(&amp;quot;jsoncallback&amp;quot;) 得到jquery端随后要回调的js function \nname:jsonp1236827957501 然后 response的内容为一个Script \nTags:&amp;quot;jsonp1236827957501(&amp;quot;+按请求参数生成的json数组+&amp;quot;)&amp;quot;; jquery就会通过回调方法动态加载调用这个js \ntag:jsonp1236827957501(json数组); 这样就达到了跨域数据交换的目的。&lt;/p&gt;&lt;p&gt;　　JSONP原理&lt;/p&gt;&lt;p&gt;　　JSONP的最基本的原理是：动态添加一个 &amp;lt;script&amp;gt;标签，而script标签的src属性是没有跨域的限制的。这样说来，这种跨域方式其实与ajax XmlHttpRequest协议无关了。&lt;/p&gt;&lt;p&gt;　　这样其实&amp;quot;jQuery AJAX跨域问题&amp;quot;就成了个伪命题，jquery $.ajax方法名有误导人之嫌。&lt;/p&gt;&lt;p&gt;　\n　如果设为dataType: &amp;#39;jsonp&amp;#39;，这个$.ajax方法就和ajax \nXmlHttpRequest没什么关系了，取而代之的则是JSONP协议。JSONP是一个非官方的协议，它允许在服务器端集成Script \ntags返回至客户端，通过javascript callback的形式实现跨域访问。&lt;/p&gt;&lt;p&gt;　　JSONP即JSON with \nPadding。由于同源策略的限制，XmlHttpRequest只允许请求当前源（域名、协议、端口）的资源。如果要进行跨域请求， \n我们可以通过使用html的script标记来进行跨域请求，并在响应中返回要执行的script代码，其中可以直接使用JSON传递 \njavascript对象。 这种跨域的通讯方式称为JSONP。&lt;/p&gt;&lt;p&gt;　　jsonCallback 函数jsonp1236827957501(....)：是浏览器客户端注册的，获取跨域服务器上的json数据后，回调的函数&lt;/p&gt;&lt;p&gt;　　Jsonp的执行过程如下：&lt;/p&gt;&lt;p&gt;　\n　首先在客户端注册一个callback (如:&amp;#39;jsoncallback&amp;#39;), \n然后把callback的名字(如:jsonp1236827957501)传给服务器。注意：服务端得到callback的数值后，要用 \njsonp1236827957501(......)把将要输出的json内容包括起来，此时，服务器生成 json 数据才能被客户端正确接收。&lt;/p&gt;&lt;p&gt;　　然后以 javascript 语法的方式，生成一个function， function 名字就是传递上来的参数 &amp;#39;jsoncallback&amp;#39;的值 jsonp1236827957501 .&lt;/p&gt;&lt;p&gt;　　最后将 json 数据直接以入参的方式，放置到 function 中，这样就生成了一段 js 语法的文档，返回给客户端。&lt;/p&gt;&lt;p&gt;　\n　客户端浏览器，解析script标签，并执行返回的 javascript 文档，此时javascript文档数据，作为参数， \n传入到了客户端预先定义好的 callback 函数(如上例中jquery $.ajax()方法封装的的success: function \n(json))里。&lt;/p&gt;&lt;p&gt;　　可以说jsonp的方式原理上和 &amp;lt;script src=&amp;quot;http://跨域/...xx.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;/p&gt;&lt;p&gt;　　是一致的(qq空间就是大量采用这种方式来实现跨域数据交换 的)。JSONP是一种脚本注入(Script Injection)行为，所以有一定的安全隐患。&lt;/p&gt;&lt;p&gt;　　那jquery为什么不支持post方式跨域呢？&lt;/p&gt;&lt;p&gt;　　虽然采用post+动态生成iframe是可以达到post跨域的目的(有位js牛人就是这样把jquery1.2.5 打patch的)，但这样做是一个比较极端的方式，不建议采用。&lt;/p&gt;&lt;p&gt;　　也可以说get方式的跨域是合法的，post方式从安全角度上，被认为是不合法的，万不得已还是不要剑走偏锋。&lt;/p&gt;&lt;p&gt;　　client端跨域访问的需求看来也引起w3c的注意了，看资料说html5 WebSocket标准支持跨域的数据交换，应该也是一个将来可选的跨域数据交换的解决方案。&lt;/p&gt;&lt;p&gt;　　来个超简单的例子：&lt;/p&gt;&lt;p&gt;Html代码&lt;/p&gt;&lt;ol class=&quot;dp-xml list-paddingleft-2&quot; start=&quot;1&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;tag&quot;&gt;&amp;lt;&lt;/span&gt;&lt;span class=&quot;tag-name&quot;&gt;title&lt;/span&gt;&lt;span class=&quot;tag&quot;&gt;&amp;gt;&lt;/span&gt;Test&amp;nbsp;Jsonp&lt;span class=&quot;tag&quot;&gt;&amp;lt;/&lt;/span&gt;&lt;span class=&quot;tag-name&quot;&gt;title&lt;/span&gt;&lt;span class=&quot;tag&quot;&gt;&amp;gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;tag&quot;&gt;&amp;lt;&lt;/span&gt;&lt;span class=&quot;tag-name&quot;&gt;script&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;attribute&quot;&gt;type&lt;/span&gt;=&lt;span class=&quot;attribute-value&quot;&gt;&amp;quot;text/javascript&amp;quot;&lt;/span&gt;&lt;span class=&quot;tag&quot;&gt;&amp;gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;jsonpCallback(result)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;alert(result.msg);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=&quot;tag&quot;&gt;&amp;lt;/&lt;/span&gt;&lt;span class=&quot;tag-name&quot;&gt;script&lt;/span&gt;&lt;span class=&quot;tag&quot;&gt;&amp;gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span class=&quot;tag&quot;&gt;&amp;lt;&lt;/span&gt;&lt;span class=&quot;tag-name&quot;&gt;script&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;attribute&quot;&gt;type&lt;/span&gt;=&lt;span class=&quot;attribute-value&quot;&gt;&amp;quot;text/javascript&amp;quot;&lt;/span&gt;&amp;nbsp;&lt;span class=&quot;attribute&quot;&gt;src&lt;/span&gt;=&lt;span class=&quot;attribute-value&quot;&gt;&amp;quot;http://crossdomain.com/jsonServerResponse?jsonp=jsonpCallback&amp;quot;&lt;/span&gt;&lt;span class=&quot;tag&quot;&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;tag&quot;&gt;&amp;lt;/&lt;/span&gt;&lt;span class=&quot;tag-name&quot;&gt;script&lt;/span&gt;&lt;span class=&quot;tag&quot;&gt;&amp;gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;　\n　其中 jsonCallback 是客户端注册的，获取跨域服务器上的json数据后，回调的函数。http://crossdomain.com \n/jsonServerResponse?jsonp=jsonpCallback 这个 url 是跨域服务器取 json \n数据的接口，参数为回调函数的名字，返回的格式为：jsonpCallback({msg:&amp;#39;this is json data&amp;#39;})&lt;/p&gt;&lt;p&gt;　\n　简述原理与过程：首先在客户端注册一个callback, 然后把callback的名字传给服务器。此时，服务器先生成 json 数据。 然后以 \njavascript 语法的方式，生成一个function , function 名字就是传递上来的参数 jsonp。最后将 json \n数据直接以入参的方式，放置到 function 中，这样就生成了一段 js 语法的文档，返回给客户端。&lt;/p&gt;&lt;p&gt;　　客户端浏览器，解析script标签，并执行返回的 javascript 文档，此时数据作为参数，传入到了客户端预先定义好的 callback 函数里。（动态执行回调函数）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;转载自：白月网&lt;br/&gt;&lt;/p&gt;', 12, 1, 1, 1444989600),
	(19, 18, 3, 1, 'MAC系统XAMPP 中 MySQL命令行客户端配置使用 ', '&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size:18px&quot;&gt;MySQL客户端&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:18px; font-weight:bold&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:12px&quot;&gt;MySQL安装包里面，在一个名为bin的文件夹，放置了很多工具包，但是使用他们的方式是命令行（ps：最近上瘾了）。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 在MAC系统，使用命令行的工具可以使用系统自带的Terminal:&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;img src=&quot;http://img.blog.csdn.net/20140616161819640?watermark/2/text/aHR0cDovL2Jsb2cuY3Nkbi5uZXQvanNfZGFkYQ==/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70/gravity/Center&quot; alt=&quot;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 顺便在这提一下，笔者使用的php＋mysql环境是MAC版的XAMPP，MySQL的客户端工具就放置在XAMPP里面的bin文件夹。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 但是如何才能使用这客户端？&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 在Terminal进入到XAMPP的bin文件夹，输入命令：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;[sql]&lt;/strong&gt; &lt;a href=&quot;http://blog.csdn.net/js_dada/article/details/31388235#&quot; class=&quot;ViewSource&quot; title=&quot;view plain&quot;&gt;view plain&lt;/a&gt;&lt;a href=&quot;http://blog.csdn.net/js_dada/article/details/31388235#&quot; class=&quot;CopyToClipboard&quot; title=&quot;copy&quot;&gt;copy&lt;/a&gt;&lt;a href=&quot;https://code.csdn.net/snippets/394673&quot; target=&quot;_blank&quot; title=&quot;在CODE上查看代码片&quot; style=&quot;text-indent:0;&quot;&gt;&lt;img src=&quot;https://code.csdn.net/assets/CODE_ico.png&quot; alt=&quot;在CODE上查看代码片&quot; style=&quot;position:relative;top:1px;left:2px;&quot; height=&quot;12&quot; width=&quot;12&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;https://code.csdn.net/snippets/394673/fork&quot; target=&quot;_blank&quot; title=&quot;派生到我的代码片&quot; style=&quot;text-indent:0;&quot;&gt;&lt;img src=&quot;https://code.csdn.net/assets/ico_fork.svg&quot; alt=&quot;派生到我的代码片&quot; style=&quot;position:relative;top:2px;left:2px;&quot; height=&quot;12&quot; width=&quot;12&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;ol class=&quot;dp-sql list-paddingleft-2&quot; start=&quot;1&quot;&gt;&lt;li&gt;&lt;p&gt;mysql&amp;nbsp;-u&amp;nbsp;root&amp;nbsp;-p&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 并没有笔者想要的结果，应该是提示输入密码的呀？？？？？？&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 在Terminal反馈给我的确是 : command not found&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;what ? 明明就在面前，如何not found，这下苦逼了！好吧，看来需要花点时间找找问题所在了。&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;经过很长很长很长...............的时间里，终于....&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;原来当你输入命令的时间，系统会在/usr/bin这个位置里寻找你输入的命令，如果你没有把命令引入到这个位置，无论你直接cd到工具具体的位置调用，也是白费&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 功夫的。只要把这个工具的绝对位置引入到/usr/bin，所有的问题就迎刃而解了，只要我们把这条命令执行：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;[plain]&lt;/strong&gt; &lt;a href=&quot;http://blog.csdn.net/js_dada/article/details/31388235#&quot; class=&quot;ViewSource&quot; title=&quot;view plain&quot;&gt;view plain&lt;/a&gt;&lt;a href=&quot;http://blog.csdn.net/js_dada/article/details/31388235#&quot; class=&quot;CopyToClipboard&quot; title=&quot;copy&quot;&gt;copy&lt;/a&gt;&lt;a href=&quot;https://code.csdn.net/snippets/394673&quot; target=&quot;_blank&quot; title=&quot;在CODE上查看代码片&quot; style=&quot;text-indent:0;&quot;&gt;&lt;img src=&quot;https://code.csdn.net/assets/CODE_ico.png&quot; alt=&quot;在CODE上查看代码片&quot; style=&quot;position:relative;top:1px;left:2px;&quot; height=&quot;12&quot; width=&quot;12&quot;/&gt;&lt;/a&gt;&lt;a href=&quot;https://code.csdn.net/snippets/394673/fork&quot; target=&quot;_blank&quot; title=&quot;派生到我的代码片&quot; style=&quot;text-indent:0;&quot;&gt;&lt;img src=&quot;https://code.csdn.net/assets/ico_fork.svg&quot; alt=&quot;派生到我的代码片&quot; style=&quot;position:relative;top:2px;left:2px;&quot; height=&quot;12&quot; width=&quot;12&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; start=&quot;1&quot;&gt;&lt;li&gt;&lt;p&gt;ln&amp;nbsp;-s&amp;nbsp;/applications/xampp/bin/mysql&amp;nbsp;/usr/bin&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;span style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;这个时候，我们再输入链接数据库命令，然后：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;img src=&quot;http://img.blog.csdn.net/20140616163422578?watermark/2/text/aHR0cDovL2Jsb2cuY3Nkbi5uZXQvanNfZGFkYQ==/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70/gravity/Center&quot; alt=&quot;&quot;/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;好吧，你成功了，输入密码，就可以尽情查看管理你的MySQL了！！！！！！！！&lt;/p&gt;&lt;div id=&quot;xunlei_com_thunder_helper_plugin_d462f475-c18e-46be-bd10-327458d045bd&quot;&gt;&lt;/div&gt;', 0, 1, 1, 1445096676),
	(20, 18, 3, 1, '恢复数据库出现错误ERROR at line 3289: Unknown command \'\\\'\'.', '&lt;p&gt;C:\\Users\\Administrator\\Desktop&amp;gt;mysql -uroot -p &lt;strong&gt;&lt;span style=&quot;background-color: #ffffff; color: #ff0000;&quot;&gt;--default-character-set=utf8&lt;/span&gt;&lt;/strong&gt; db&amp;lt;db.sql&lt;/p&gt;', 0, 1, 1, 1445217531),
	(21, 18, 1, 1, 'PHP中全局变量global和$GLOBALS[]的区别分析', '&lt;p style=&quot;text-align: left;&quot;&gt;$GLOBALS[&amp;#39;var&amp;#39;]是外部的全局变量本身,global $var是外部$var的同名引用或者指针&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;strong&gt;一、举例比较&lt;/strong&gt; &lt;br style=&quot;text-align: left;&quot;/&gt;例一： &lt;br style=&quot;text-align: left;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;a style=&quot;CURSOR: pointer&quot; id=&quot;copybut85473&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php&amp;nbsp;\n$var1&amp;nbsp;=&amp;nbsp;1;&amp;nbsp;\nfunction&amp;nbsp;test(){&amp;nbsp;\nunset($GLOBALS[&amp;#39;var1&amp;#39;]);&amp;nbsp;\n}&amp;nbsp;\ntest();&amp;nbsp;\necho&amp;nbsp;$var1;&amp;nbsp;\n?&amp;gt;&lt;/pre&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;br style=&quot;text-align: left;&quot;/&gt;因为$var1被删除了，所以什么东西都没有打印。 &lt;br style=&quot;text-align: left;&quot;/&gt;例二： &lt;br style=&quot;text-align: left;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;a style=&quot;CURSOR: pointer&quot; id=&quot;copybut77187&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;复制代码&lt;/span&gt;&lt;/a&gt; 代码如下:&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php&amp;nbsp;\n$var1&amp;nbsp;=&amp;nbsp;1;&amp;nbsp;\nfunction&amp;nbsp;test(){&amp;nbsp;\nglobal&amp;nbsp;$var1;&amp;nbsp;\nunset($var1);&amp;nbsp;\n}&amp;nbsp;\ntest();&amp;nbsp;\necho&amp;nbsp;$var1;&amp;nbsp;\n?&amp;gt;&lt;/pre&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;br style=&quot;text-align: left;&quot;/&gt;意外的打印了1。证明删除的只是别名引用，其本身的值没有受到任何的改变。 &lt;br style=&quot;text-align: left;&quot;/&gt;&lt;br style=&quot;text-align: left;&quot;/&gt;&lt;strong&gt;二、解释 &lt;br style=&quot;text-align: left;&quot;/&gt;&lt;/strong&gt;global $var其实就是&amp;amp;$GLOBALS[&amp;#39;var&amp;#39;]，调用外部变量的一个别名而已。 &lt;br style=&quot;text-align: left;&quot;/&gt;上面代码中的$var1和$GLOBALS[&amp;#39;var1&amp;#39;]是指的同一变量，而不是两个不同的变量。 &lt;br style=&quot;text-align: left;&quot;/&gt;PHP的全局变量和C语言有一点点不同。在C语言中，全局变量在函数中主动生效，除非被局部变量笼罩。这可能引起一些问题，有些人可能漫不经心的转变一个全局变量。PHP中全局变量在函数中应用时必须用global申明为全局。 &lt;br style=&quot;text-align: left;&quot;/&gt;PHP的Global变量的作用是定义全局变量，但是这个全局变量不是应用于整个网站，而是应用于当前页面，包括include或require的所有文件。 &lt;br style=&quot;text-align: left;&quot;/&gt;&lt;br style=&quot;text-align: left;&quot;/&gt;&lt;strong&gt;三、结论 &lt;br style=&quot;text-align: left;&quot;/&gt;&lt;/strong&gt;1.$GLOBALS[&amp;#39;var&amp;#39;]是外部的全局变量本身 &lt;br style=&quot;text-align: left;&quot;/&gt;2.global $var是外部$var的同名引用或者指针。&lt;/p&gt;&lt;p&gt;&lt;br style=&quot;text-align: left;&quot;/&gt;&lt;/p&gt;', 3, 1, 1, 1445217758),
	(22, 18, 14, 1, '.vimrc', '&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;set&amp;nbsp;number\nset&amp;nbsp;history&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=400\nset&amp;nbsp;ignorecase\nset&amp;nbsp;tags&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=./tags,tags,./../tags,./**/tags;\nset&amp;nbsp;sessionoptions&amp;nbsp;-=curdir\nset&amp;nbsp;sessionoptions&amp;nbsp;+=sesdir\nset&amp;nbsp;cul\nfiletype&amp;nbsp;plugin&amp;nbsp;indent&amp;nbsp;on\nset&amp;nbsp;autoindent\nset&amp;nbsp;tabstop&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=4\nset&amp;nbsp;softtabstop&amp;nbsp;=4\nset&amp;nbsp;shiftwidth&amp;nbsp;&amp;nbsp;=4\nset&amp;nbsp;expandtab\nset&amp;nbsp;smarttab\nset&amp;nbsp;smartindent\nau&amp;nbsp;FileType&amp;nbsp;html,python,vim,javascript&amp;nbsp;setlocal&amp;nbsp;shiftwidth=2\nset&amp;nbsp;hidden\nset&amp;nbsp;encoding=utf8\nset&amp;nbsp;fileencodings=utf-8,gb2312,ucs-bom,cp936,big5,latin1\nset&amp;nbsp;termencoding=utf-8,gb2312\nset&amp;nbsp;laststatus=2\nset&amp;nbsp;cmdheight=2\nset&amp;nbsp;acd\nset&amp;nbsp;paste\n&amp;quot;set&amp;nbsp;fdm=marker\n&amp;quot;set&amp;nbsp;fdm=indent\n&amp;quot;&amp;quot;color&amp;nbsp;shine\ncolor&amp;nbsp;evening\n&amp;quot;color&amp;nbsp;darkblue\n&amp;quot;&amp;quot;color&amp;nbsp;delek\n&amp;quot;color&amp;nbsp;torte&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1, 1, 1445218716),
	(23, 18, 3, 1, 'linux中转移mysql默认安装目录', '&lt;p&gt;1、停止mysql进程&lt;/p&gt;&lt;p&gt;2、把/var/lib/mysql整个目录移到/data0/db&lt;/p&gt;&lt;p&gt;mv /var/lib/mysql　/home/data/&lt;/p&gt;&lt;p&gt;这样就把MySQL的数据文件移动到了/data0/db/mysql下&lt;/p&gt;&lt;p&gt;3、修改my.cnf配置文件&lt;/p&gt;&lt;p&gt;不要直接使用/etc/my.cnf配置文件，请到/usr/share/mysql/下找到*.cnf文件，拷贝其中一个（我是my-medium.cnf）到/etc/并改名为my.cnf)中。命令如下：&lt;/p&gt;&lt;p&gt;[root@test1 mysql]# cp /usr/share/mysql/my-medium.cnf　/etc/my.cnf&lt;/p&gt;&lt;p&gt;4、编辑MySQL的配置文件/etc/my.cnf&lt;/p&gt;&lt;p&gt;为保证MySQL能够正常工作，需要指明mysql.sock文件的产生位置。&lt;/p&gt;&lt;p&gt;# The following options will be passed to all MySQL clients&amp;nbsp;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;[client]&amp;nbsp;\n#password&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;your_password&amp;nbsp;\nport&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;3306&amp;nbsp;\n#socket&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;/var/lib/mysql/mysql.sock&amp;nbsp;&amp;nbsp;#注释了&amp;nbsp;\nsocket=/data0/db/mysql/mysql.sock&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#新加的内容&amp;nbsp;\n#&amp;nbsp;Here&amp;nbsp;follows&amp;nbsp;entries&amp;nbsp;for&amp;nbsp;some&amp;nbsp;specific&amp;nbsp;programs&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;\n#&amp;nbsp;The&amp;nbsp;MySQL&amp;nbsp;server&amp;nbsp;\n[mysqld]&amp;nbsp;\nport&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;3306&amp;nbsp;\n#socket&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;/var/lib/mysql/mysql.sock&amp;nbsp;&amp;nbsp;#注释了&amp;nbsp;\ndatadir=/data0/db/mysql&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#新加的内容&amp;nbsp;\nsocket=/data0/db/mysql/mysql.sock&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#新加的内容&lt;/pre&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;word-break: break-all;&quot; class=&quot;code&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;5、MySQL启动脚本/etc/rc.d/init.d/mysqld 不用做任何修改&lt;/p&gt;&lt;p&gt;我的版本是Server version: 5.0.95，其它版要不要改不知道 。&lt;/p&gt;&lt;p&gt;6、重新启动MySQL服务&lt;/p&gt;&lt;p&gt;/etc/rc.d/init.d/mysqld　start&lt;/p&gt;&lt;p&gt;如果工作正常移动就成功了，&lt;/p&gt;&lt;p&gt;移动完MySql之后，PHP连接不上Mysql数据库了&lt;/p&gt;&lt;p&gt;-----------------------------------------------------&lt;/p&gt;&lt;p&gt;经过上面的的操作，把MySql的数据文件移到其它目录后，PHP连接不上MySql数据库了，是因为找不mysql.sock文件。&lt;/p&gt;&lt;p&gt;报错信息如下：&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;Discuz!&amp;nbsp;Database&amp;nbsp;Error&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;(2002)&amp;nbsp;notconnect&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;解决方法一：&lt;/p&gt;&lt;p&gt;建一个软连接&lt;/p&gt;&lt;p&gt;cd&amp;nbsp;/var/lib&lt;/p&gt;&lt;p&gt;mkdir mysql&lt;/p&gt;&lt;p&gt;ln -s &amp;nbsp;/data0/db/mysql/mysql.sock &amp;nbsp;/var/lib/mysql/mysql.sock&lt;/p&gt;&lt;p&gt;解决方案二：&lt;/p&gt;&lt;p&gt;修改/etc/php.ini文件&lt;/p&gt;&lt;p&gt;找到 &amp;nbsp;mysql.default_socket = &amp;nbsp; 一行，这个值一开始是空的，php将会使用内建在mysql中的默认值。&lt;/p&gt;&lt;p&gt;修改为以下内容：&lt;/p&gt;&lt;p&gt;mysql.default_socket = /data0/db/mysql/mysql.sock&lt;/p&gt;&lt;p&gt;mysql.sock的文件位置请跟据你的实际情况填写。&lt;/p&gt;&lt;p&gt;还有一处mysqli.default_socket =，要不要修改视情况吧，我没有修改。&lt;/p&gt;', 5, 1, 1, 1445218907),
	(24, 18, 9, 1, 'Linux crontab 命令格式与详细例子', '&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;基本格式&amp;nbsp;:&lt;br/&gt;*　　*　　*　　*　　*　　command&lt;br/&gt;分　时　日　月　周　命令&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;第1列表示分钟1～59&amp;nbsp;每分钟用*或者&amp;nbsp;*/1表示&lt;br/&gt;第2列表示小时1～23（0表示0点）&lt;br/&gt;第3列表示日期1～31&lt;br/&gt;第4列表示月份1～12&lt;br/&gt;第5列标识号星期0～6（0表示星期天）&lt;br/&gt;第6列要运行的命令&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab文件的一些例子：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;30&amp;nbsp;21&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;上面的例子表示每晚的21:30重启apache。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;45&amp;nbsp;4&amp;nbsp;1,10,22&amp;nbsp;*&amp;nbsp;*&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;上面的例子表示每月1、10、22日的4&amp;nbsp;:&amp;nbsp;45重启apache。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;10&amp;nbsp;1&amp;nbsp;*&amp;nbsp;*&amp;nbsp;6,0&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;上面的例子表示每周六、周日的1&amp;nbsp;:&amp;nbsp;10重启apache。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0,30&amp;nbsp;18-23&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;上面的例子表示在每天18&amp;nbsp;:&amp;nbsp;00至23&amp;nbsp;:&amp;nbsp;00之间每隔30分钟重启apache。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;23&amp;nbsp;*&amp;nbsp;*&amp;nbsp;6&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;上面的例子表示每星期六的11&amp;nbsp;:&amp;nbsp;00&amp;nbsp;pm重启apache。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;*&amp;nbsp;*/1&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;每一小时重启apache&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;*&amp;nbsp;23-7/1&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;晚上11点到早上7点之间，每隔一小时重启apache&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;11&amp;nbsp;4&amp;nbsp;*&amp;nbsp;mon-wed&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;每月的4号与每周一到周三的11点重启apache&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;4&amp;nbsp;1&amp;nbsp;jan&amp;nbsp;*&amp;nbsp;/usr/local/etc/rc.d/lighttpd&amp;nbsp;restart&lt;br/&gt;一月一号的4点重启apache&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;名称&amp;nbsp;:&amp;nbsp;crontab&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;使用权限&amp;nbsp;:&amp;nbsp;所有使用者&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;使用方式&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab&amp;nbsp;file&amp;nbsp;[-u&amp;nbsp;user]-用指定的文件替代目前的crontab。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab-[-u&amp;nbsp;user]-用标准输入替代目前的crontab.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab-1[user]-列出用户目前的crontab.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab-e[user]-编辑用户目前的crontab.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab-d[user]-删除用户目前的crontab.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab-c&amp;nbsp;dir-&amp;nbsp;指定crontab的目录。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab文件的格式：M&amp;nbsp;H&amp;nbsp;D&amp;nbsp;m&amp;nbsp;d&amp;nbsp;cmd.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;M:&amp;nbsp;分钟（0-59）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;H：小时（0-23）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;D：天（1-31）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;m:&amp;nbsp;月（1-12）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;d:&amp;nbsp;一星期内的天（0~6，0为星期天）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;cmd要运行的程序，程序被送入sh执行，这个shell只有USER,HOME,SHELL这三个环境变量&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;说明&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab&amp;nbsp;是用来让使用者在固定时间或固定间隔执行程序之用，换句话说，也就是类似使用者的时程表。-u&amp;nbsp;user&amp;nbsp;是指设定指定&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;user&amp;nbsp;的时程表，这个前提是你必须要有其权限(比如说是&amp;nbsp;root)才能够指定他人的时程表。如果不使用&amp;nbsp;-u&amp;nbsp;user&amp;nbsp;的话，就是表示设&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;定自己的时程表。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;参数&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab&amp;nbsp;-e&amp;nbsp;:&amp;nbsp;执行文字编辑器来设定时程表，内定的文字编辑器是&amp;nbsp;VI，如果你想用别的文字编辑器，则请先设定&amp;nbsp;VISUAL&amp;nbsp;环境变数&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;来指定使用那个文字编辑器(比如说&amp;nbsp;setenv&amp;nbsp;VISUAL&amp;nbsp;joe)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab&amp;nbsp;-r&amp;nbsp;:&amp;nbsp;删除目前的时程表&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab&amp;nbsp;-l&amp;nbsp;:&amp;nbsp;列出目前的时程表&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;crontab&amp;nbsp;file&amp;nbsp;[-u&amp;nbsp;user]-用指定的文件替代目前的crontab。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;时程表的格式如下&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;f1&amp;nbsp;f2&amp;nbsp;f3&amp;nbsp;f4&amp;nbsp;f5&amp;nbsp;program&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;其中&amp;nbsp;f1&amp;nbsp;是表示分钟，f2&amp;nbsp;表示小时，f3&amp;nbsp;表示一个月份中的第几日，f4&amp;nbsp;表示月份，f5&amp;nbsp;表示一个星期中的第几天。program&amp;nbsp;表示要执&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;行的程序。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;当&amp;nbsp;f1&amp;nbsp;为&amp;nbsp;*&amp;nbsp;时表示每分钟都要执行&amp;nbsp;program，f2&amp;nbsp;为&amp;nbsp;*&amp;nbsp;时表示每小时都要执行程序，其馀类推&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;当&amp;nbsp;f1&amp;nbsp;为&amp;nbsp;a-b&amp;nbsp;时表示从第&amp;nbsp;a&amp;nbsp;分钟到第&amp;nbsp;b&amp;nbsp;分钟这段时间内要执行，f2&amp;nbsp;为&amp;nbsp;a-b&amp;nbsp;时表示从第&amp;nbsp;a&amp;nbsp;到第&amp;nbsp;b&amp;nbsp;小时都要执行，其馀类推&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;当&amp;nbsp;f1&amp;nbsp;为&amp;nbsp;*/n&amp;nbsp;时表示每&amp;nbsp;n&amp;nbsp;分钟个时间间隔执行一次，f2&amp;nbsp;为&amp;nbsp;*/n&amp;nbsp;表示每&amp;nbsp;n&amp;nbsp;小时个时间间隔执行一次，其馀类推&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;当&amp;nbsp;f1&amp;nbsp;为&amp;nbsp;a,&amp;nbsp;b,&amp;nbsp;c,...&amp;nbsp;时表示第&amp;nbsp;a,&amp;nbsp;b,&amp;nbsp;c,...&amp;nbsp;分钟要执行，f2&amp;nbsp;为&amp;nbsp;a,&amp;nbsp;b,&amp;nbsp;c,...&amp;nbsp;时表示第&amp;nbsp;a,&amp;nbsp;b,&amp;nbsp;c...个小时要执行，其馀类推&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;使用者也可以将所有的设定先存放在档案&amp;nbsp;file&amp;nbsp;中，用&amp;nbsp;crontab&amp;nbsp;file&amp;nbsp;的方式来设定时程表。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;例子&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;#每天早上7点执行一次&amp;nbsp;/bin/ls&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;7&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;/bin/ls&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;在&amp;nbsp;12&amp;nbsp;月内,&amp;nbsp;每天的早上&amp;nbsp;6&amp;nbsp;点到&amp;nbsp;12&amp;nbsp;点中，每隔3个小时执行一次&amp;nbsp;/usr/bin/backup&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;6-12/3&amp;nbsp;*&amp;nbsp;12&amp;nbsp;*&amp;nbsp;/usr/bin/backup&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;周一到周五每天下午&amp;nbsp;5:00&amp;nbsp;寄一封信给&amp;nbsp;alex@domain.name&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;17&amp;nbsp;*&amp;nbsp;*&amp;nbsp;1-5&amp;nbsp;mail&amp;nbsp;-s&amp;nbsp;&amp;quot;hi&amp;quot;&amp;nbsp;alex@domain.name&amp;nbsp;&amp;lt;&amp;nbsp;/tmp/maildata&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;每月每天的午夜&amp;nbsp;0&amp;nbsp;点&amp;nbsp;20&amp;nbsp;分,&amp;nbsp;2&amp;nbsp;点&amp;nbsp;20&amp;nbsp;分,&amp;nbsp;4&amp;nbsp;点&amp;nbsp;20&amp;nbsp;分....执行&amp;nbsp;echo&amp;nbsp;&amp;quot;haha&amp;quot;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;20&amp;nbsp;0-23/2&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;echo&amp;nbsp;&amp;quot;haha&amp;quot;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;注意&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;当程序在你所指定的时间执行后，系统会寄一封信给你，显示该程序执行的内容，若是你不希望收到这样的信，请在每一行空一格之&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;后加上&amp;nbsp;&amp;gt;&amp;nbsp;/dev/null&amp;nbsp;2&amp;gt;&amp;amp;1&amp;nbsp;即可&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;例子2&amp;nbsp;:&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;#每天早上6点10分&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;10&amp;nbsp;6&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;date&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;#每两个小时&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;*/2&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;date&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;#晚上11点到早上8点之间每两个小时，早上8点&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;23-7/2，8&amp;nbsp;*&amp;nbsp;*&amp;nbsp;*&amp;nbsp;date&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;#每个月的4号和每个礼拜的礼拜一到礼拜三的早上11点&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;11&amp;nbsp;4&amp;nbsp;*&amp;nbsp;mon-wed&amp;nbsp;date&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;#1月份日早上4点&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;0&amp;nbsp;4&amp;nbsp;1&amp;nbsp;jan&amp;nbsp;*&amp;nbsp;date&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;范例&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left; font-family: Arial; color: #333333; background-color: #ffffff; line-height: 26px;&quot;&gt;&lt;span style=&quot;font-size: 10pt;&quot;&gt;$crontab&amp;nbsp;-l&amp;nbsp;列出用户目前的crontab.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1, 1, 1445219312),
	(25, 18, 8, 1, 'js页面跳转', '&lt;p&gt;&lt;strong&gt;1.按钮式：&amp;nbsp; &lt;/strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;INPUT&amp;nbsp;name=&amp;quot;pclog&amp;quot;&amp;nbsp;type=&amp;quot;button&amp;quot;&amp;nbsp;value=&amp;quot;GO&amp;quot;&amp;nbsp;onClick=&amp;quot;location.href=&amp;#39;http://www.ddhbb.com/&amp;#39;&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2.链接式：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　&amp;lt;a&amp;nbsp;href=&amp;quot;javascript:history.go(-1)&amp;quot;&amp;gt;返回上一步&amp;lt;/a&amp;gt;&lt;/p&gt;&lt;p&gt;　　&amp;lt;a&amp;nbsp;href=&amp;quot;&amp;lt;%=Request.ServerVariables(&amp;quot;HTTP_REFERER&amp;quot;)%&amp;gt;&amp;quot;&amp;gt;返回上一步&amp;lt;/a&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3.直接跳转式：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　&amp;lt;script&amp;gt;window.location.href=&amp;#39;http://www.ddhbb.com&amp;#39;;&amp;lt;/script&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4.开新窗口：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;　　&amp;lt;a&amp;nbsp;href=&amp;quot;javascript:&amp;quot;&amp;nbsp;\nonClick=&amp;quot;window.open(&amp;#39;http://www.ddhbb.com/blog\n/guestbook.asp&amp;#39;,&amp;#39;&amp;#39;,&amp;#39;height=500,width=611,scrollbars=yes,status=yes&amp;#39;)&amp;quot;&amp;amp;\ngt;布足迹&amp;lt;/a&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5.定时跳转&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;lt;script&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;setTimeout(&amp;quot;location.href=&amp;#39;http://baidu.com&amp;#39;&amp;quot;,&amp;nbsp;3000);&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;lt;/script&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6.定时跳转到新窗口&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;lt;script&amp;gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; setTimeout(function(){window.open(&amp;#39;http://baidu.com&amp;#39;),&amp;nbsp;3000);&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/script&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 0, 1, 1, 1445220279),
	(26, 18, 8, 1, 'JS实现动态显示当前时间(12/24小时制)', '&lt;pre class=&quot;brush:html;toolbar:false;&quot;&gt;function&amp;nbsp;nowTime(ev,type){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;ev:显示时间的元素\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;type:时间显示模式.若传入12则为12小时制,不传入则为24小时制\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//年月日时分秒\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;Y,M,D,W,H,I,S;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//月日时分秒为单位时前面补零\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;fillZero(v){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(v&amp;lt;10){v=&amp;#39;0&amp;#39;+v;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;v;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;(function(){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;d=new&amp;nbsp;Date();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;Week=[&amp;#39;星期天&amp;#39;,&amp;#39;星期一&amp;#39;,&amp;#39;星期二&amp;#39;,&amp;#39;星期三&amp;#39;,&amp;#39;星期四&amp;#39;,&amp;#39;星期五&amp;#39;,&amp;#39;星期六&amp;#39;];\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Y=d.getFullYear();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;M=fillZero(d.getMonth()+1);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;D=fillZero(d.getDate());\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;W=Week[d.getDay()];\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;H=fillZero(d.getHours());\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;I=fillZero(d.getMinutes());\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;S=fillZero(d.getSeconds());\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//12小时制显示模式\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(type&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;type==12){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//若要显示更多时间类型诸如中午凌晨可在下面添加判断\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(H&amp;lt;=12){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;H=&amp;#39;上午&amp;amp;nbsp;&amp;#39;+H;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;if(H&amp;gt;12&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;H&amp;lt;24){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;H-=12;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;H=&amp;#39;下午&amp;amp;nbsp;&amp;#39;+fillZero(H);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;if(H==24){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;H=&amp;#39;下午&amp;amp;nbsp;00&amp;#39;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ev.innerHTML=Y+&amp;#39;年&amp;#39;+M+&amp;#39;月&amp;#39;+D+&amp;#39;日&amp;nbsp;&amp;#39;+&amp;#39;&amp;nbsp;&amp;#39;+W+&amp;#39;&amp;amp;nbsp;&amp;#39;+H+&amp;#39;:&amp;#39;+I+&amp;#39;:&amp;#39;+S;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//每秒更新时间\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;setTimeout(arguments.callee,1000);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;})();\n}&lt;/pre&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;//24小时制调用\nnowTime(document.getElementById(&amp;#39;time24&amp;#39;));\n//12小时制调用\nnowTime(document.getElementById(&amp;#39;time12&amp;#39;),12);&lt;/pre&gt;&lt;pre id=&quot;line1&quot;&gt;&amp;lt;!--DEMO&amp;nbsp;start--&amp;gt;&amp;lt;div&amp;nbsp;id=&amp;quot;demo&amp;quot;&amp;gt;	\n&amp;lt;h2&amp;nbsp;title=&amp;quot;当前时间&amp;quot;&amp;gt;&amp;lt;em&amp;gt;24小时制：&amp;lt;/em&amp;gt;&amp;lt;span&amp;nbsp;id=&amp;quot;time24&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;	\n&amp;lt;h2&amp;nbsp;title=&amp;quot;当前时间&amp;quot;&amp;gt;&amp;lt;em&amp;gt;12小时制：&amp;lt;/em&amp;gt;&amp;lt;span&amp;nbsp;id=&amp;quot;time12&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;/div&amp;gt;\n&amp;lt;!--DEMO&amp;nbsp;end--&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1, 1445220680),
	(27, 18, 9, 1, 'Linux利用i节点删除乱码文件', '&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑;font-size:24px&quot;&gt;&lt;span style=&quot;color:#ff0000&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size:18px&quot;&gt;应用场景：删除乱码文件&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;font-size:18px&quot;&gt; &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑;font-size:18px&quot;&gt;当文件名为乱码的时候，无法通过键盘输入文件名，所以在终端下就不能直接利用rm，mv等命令管理文件了。&lt;br/&gt;&lt;strong&gt;第一步： 获得文件的节点号：&lt;/strong&gt;&lt;br/&gt;但是我们知道每个文件都有一个i节点号，我们可以考虑通过i节点号来管理文件。首先，我们要取得文件的i节点号，这个可以通过ls命令的-i选项获得得,也可以通过ll -i 来获得。&lt;br/&gt;&lt;strong&gt;第二步，使用find命令将文件名传递给rm命&lt;/strong&gt;&lt;br/&gt;find&amp;nbsp; ./ -inum 12345 -print -exec rm {} -rf&amp;nbsp; \\;&lt;br/&gt;&lt;span style=&quot;color:#00ff00&quot;&gt;注意 \\; 前面的空格不能少，少了就会报错。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 5, 1, 1, 1445221196),
	(28, 18, 3, 1, 'MYSQL中每个用户取1条记录的三种写法(group by xxx)', '&lt;p&gt;[sql]&amp;nbsp;&lt;/p&gt;&lt;p&gt;select * from (select * from member_payment &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; order by id desc) t group by member_id limit 10 &amp;nbsp;&lt;/p&gt;&lt;p&gt;第一种是先排序，然后group，这样的话自然可以取到最适合的一条数据。&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;缺点很明显：Using temporary; Using filesort&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;[sql]&amp;nbsp;&lt;/p&gt;&lt;p&gt;select s.* &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;from (SELECT max(id) as id FROM `member_payment` group by `member_id` limit 10) t &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;left join `member_payment` as s on t.id=s.id &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;第二种是联合查询&lt;/p&gt;&lt;p&gt;[sql]&amp;nbsp;&lt;/p&gt;&lt;p&gt;select * from `member_payment` where EXISTS ( &amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; select `id` from ( &amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; SELECT max(`id`) as id FROM `member_payment` group by `member_id` limit 10) t &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; where t.`id`=`member_payment`.`id` &amp;nbsp;&lt;/p&gt;&lt;p&gt;) &amp;nbsp;&lt;/p&gt;&lt;p&gt;第三种是子查询&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;窃以为第二种效率最高&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1, 1, 1445221486),
	(29, 18, 10, 1, '为阿里云 ECS 服务器创建自定义系统镜像 ', '&lt;p&gt;&lt;span style=&quot;color:#222222&quot;&gt;您可以把配置好的 ECS 服务器的系统，比如你安装了一些特定的软件，做了特别的配置，完成以后，&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color:#222222&quot;&gt;你可以把它做成一个自定义的系统镜像，这样你就可以重复在不同的 ECS 服务器上使用这个系统镜像了。&lt;/span&gt;&lt;br/&gt;&lt;strong&gt;第一步：创建系统快照&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在阿里云后台，找到你配置好的 ECS 实例，打开 本实例磁盘，找到系统盘，然后点击 创建快照。&lt;/p&gt;&lt;p&gt;&lt;img id=&quot;aimg_lVpe1&quot; class=&quot;zoom&quot; src=&quot;http://talk.ninghao.net/uploads/default/_optimized/6cc/b0d/95ced4981e_1000x350.png&quot; style=&quot;cursor:pointer&quot; alt=&quot;&quot; border=&quot;0&quot; height=&quot;210&quot; width=&quot;600&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;第二步：创建自定义镜像&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;为系统盘创建了快照以后，你就可以基于这个快照，去创建自定义镜像了，打开 本实例快照，找到之前创建的系统盘的快照，点击 创建自定义镜像，然后为镜像起个名字，添加点描述。&lt;/p&gt;&lt;p&gt;&lt;img id=&quot;aimg_mKnbY&quot; class=&quot;zoom&quot; src=&quot;http://talk.ninghao.net/uploads/default/_optimized/9f7/0c4/5f8da103da_1000x290.png&quot; style=&quot;cursor:pointer&quot; alt=&quot;&quot; border=&quot;0&quot; height=&quot;174&quot; width=&quot;600&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;img id=&quot;aimg_Enrv1&quot; class=&quot;zoom&quot; src=&quot;http://talk.ninghao.net/uploads/default/405/7e1450124c022ae5.png&quot; alt=&quot;&quot; border=&quot;0&quot; height=&quot;463&quot; width=&quot;592&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;第三步：应用自定义镜像&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;先关掉你想使用自定义镜像的 ECS 实例，点击 更多，更换系统盘。&lt;/p&gt;&lt;p&gt;&lt;img id=&quot;aimg_o2Nnr&quot; class=&quot;zoom&quot; src=&quot;http://talk.ninghao.net/uploads/default/401/ebe2e2c9167d0cbd.png&quot; alt=&quot;&quot; border=&quot;0&quot; height=&quot;370&quot; width=&quot;278&quot;/&gt;&lt;/p&gt;&lt;p&gt;选择 自定义镜像，然后再选择之前我们创建的 自定义镜像。再为系统设置 root 用户的密码，确定以后，就会为你更换系统盘，你之前对系统所做的所有的修改都会为你保留。&lt;/p&gt;&lt;p&gt;&lt;img id=&quot;aimg_f5Z2d&quot; class=&quot;zoom&quot; src=&quot;http://talk.ninghao.net/uploads/default/404/462ba9dcb895b015.png&quot; style=&quot;cursor:pointer&quot; alt=&quot;&quot; border=&quot;0&quot; height=&quot;324&quot; width=&quot;600&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1, 1, 1445224052),
	(30, 18, 11, 1, '使用yum安装MongoDB及服务器端配置', '&lt;p&gt;安装 MongoDB 的方法有很多种，可以源代码安装，在 CentOS 也可以用yum源安装的方法。由于MongoDB更新得比较快，我比较喜欢用yum源安装的方法。64位Centos下的安装步骤如下： 1、准备工作&lt;/p&gt;&lt;p&gt;安装&lt;a href=&quot;http://www.linuxeye.com/tags.php?/MongoDB&quot; target=&quot;_blank&quot;&gt;MongoDB&lt;/a&gt;的方法有很多种，可以源代码安装，在&lt;a href=&quot;http://www.linuxeye.com/tags.php?/CentOS&quot; target=&quot;_blank&quot;&gt;CentOS&lt;/a&gt;也可以用yum源安装的方法。由于MongoDB更新得比较快，我比较喜欢用yum源安装的方法。64位Centos下的安装步骤如下：&lt;br/&gt;&lt;br/&gt;1、准备工作&lt;br/&gt;运行yum命令查看MongoDB的包信息 [root@localhost~]# yum info mongo-10gen&lt;br/&gt;&lt;br/&gt;（提示没有相关匹配的信息，） 说明你的centos系统中的yum源不包含MongoDB的相关资源，所以要在使用yum命令安装MongoDB前需要增加yum源，也就是在 /etc/yum.repos.d/目录中增加 *.repo yum源配置文件&lt;br/&gt;&lt;br/&gt;2、vi /etc/yum.repos.d/10gen.repo，输入下面的语句：&lt;/p&gt;&lt;pre style=&quot;&quot; class=&quot;prettyprint prettyprinted&quot;&gt;[10gen]\nname=10gen&amp;nbsp;Repository\nbaseurl=http://downloads-distro.mongodb.org/repo/redhat/os/x86_64\ngpgcheck=0&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;做好yum源的配置后，如果配置正确执行下面的命令便可以查询MongoDB相关的信息：&lt;br/&gt;&lt;br/&gt;查看mongoDB的服务器包的信息&lt;br/&gt;&lt;br/&gt;[root@localhost geffzhang]# yum info mongo-10gen-server&lt;br/&gt;* base: centos.ustc.edu.cn&lt;br/&gt;* extras: centos.ustc.edu.cn&lt;br/&gt;* updates: centos.ustc.edu.cn&lt;br/&gt;Installed Packages&lt;br/&gt;Name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : mongo-10gen-server&lt;br/&gt;Arch&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : x86_64&lt;br/&gt;Version&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : 2.0.6&lt;br/&gt;Release&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : mongodb_1&lt;br/&gt;Size&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : 13 M&lt;br/&gt;Repo&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : installed&lt;br/&gt;From repo&amp;nbsp;&amp;nbsp; : 10gen&lt;br/&gt;Summary&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : mongo server, sharding server, and support scripts&lt;br/&gt;URL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : http://www.mongodb.org&lt;br/&gt;License&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : AGPL 3.0&lt;br/&gt;Description : Mongo (from “huMONGOus”) is a schema-free document-oriented&lt;br/&gt;: database.&lt;br/&gt;:&lt;br/&gt;: This package provides the mongo server software, mongo sharding&lt;br/&gt;: server softwware, default configuration &lt;a href=&quot;http://www.linuxeye.com/command/file.html&quot; target=&quot;_blank&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;file&lt;/span&gt;&lt;/a&gt;s, and init.d scripts.&lt;br/&gt;查看客户端安装情况&lt;br/&gt;[root@localhost geffzhang]#&lt;br/&gt;&lt;br/&gt;* base: centos.ustc.edu.cn&lt;br/&gt;* extras: centos.ustc.edu.cn&lt;br/&gt;* updates: centos.ustc.edu.cn&lt;br/&gt;Installed Packages&lt;br/&gt;Name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : mongo-10gen&lt;br/&gt;Arch&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : x86_64&lt;br/&gt;Version&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : 2.0.6&lt;br/&gt;Release&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : mongodb_1&lt;br/&gt;Size&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : 69 M&lt;br/&gt;Repo&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : installed&lt;br/&gt;From repo&amp;nbsp;&amp;nbsp; : 10gen&lt;br/&gt;Summary&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : mongo client shell and tools&lt;br/&gt;URL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : http://www.mongodb.org&lt;br/&gt;License&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; : AGPL 3.0&lt;br/&gt;Description : Mongo (from “huMONGOus”) is a schema-free document-oriented&lt;br/&gt;: database. It features dynamic profileable queries, full indexing,&lt;br/&gt;: repli&lt;a href=&quot;http://www.linuxeye.com/command/cat.html&quot; target=&quot;_blank&quot;&gt;&lt;span style=&quot;text-decoration:underline;&quot;&gt;cat&lt;/span&gt;&lt;/a&gt;ion and fail-over support, efficient storage of large&lt;br/&gt;: binary data objects, and auto-sharding.&lt;br/&gt;:&lt;br/&gt;: This package provides the mongo shell, import/export tools, and&lt;br/&gt;: other client utilities.&lt;br/&gt;&lt;br/&gt;3、安装MongoDB的服务器端和客户端工具&lt;br/&gt;[root@localhost geffzhang]#&amp;nbsp; yum install mongo-10gen-server&lt;br/&gt;&lt;br/&gt;[root@localhost geffzhang]#&amp;nbsp; yum install mongo-10gen&lt;br/&gt;&lt;br/&gt;4、启动Mongodb&lt;br/&gt;&lt;br/&gt;service mongod start&lt;br/&gt;&lt;br/&gt;以后有更新了，停掉mongodb，执行yum update mongo-10gen mongo-10gen-server 即可。&lt;br/&gt;&lt;br/&gt;5、服务器配置: /etc/mongod.conf&lt;/p&gt;&lt;pre style=&quot;&quot; class=&quot;prettyprint prettyprinted&quot;&gt;&amp;nbsp;#&amp;nbsp;mongo.conf\n&amp;nbsp;#where&amp;nbsp;to&amp;nbsp;log\n&amp;nbsp;logpath=/var/log/mongo/mongod.log\n&amp;nbsp;\nlogappend=true&amp;nbsp;#以追加方式写入日志\n&amp;nbsp;#&amp;nbsp;fork&amp;nbsp;and&amp;nbsp;run&amp;nbsp;in&amp;nbsp;background\n&amp;nbsp;fork&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#port&amp;nbsp;=&amp;nbsp;27017&amp;nbsp;#端口\n&amp;nbsp;dbpath=/var/lib/mongo&amp;nbsp;#数据库文件保存位置\n&amp;nbsp;directoryperdb=true\n&amp;nbsp;#&amp;nbsp;Enables&amp;nbsp;periodic&amp;nbsp;logging&amp;nbsp;of&amp;nbsp;CPU&amp;nbsp;utilization&amp;nbsp;and&amp;nbsp;I/O&amp;nbsp;wait\n&amp;nbsp;#启用定期记录CPU利用率和&amp;nbsp;I/O&amp;nbsp;等待\n&amp;nbsp;#cpu&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;Turn&amp;nbsp;on/off&amp;nbsp;security.&amp;nbsp;&amp;nbsp;Off&amp;nbsp;is&amp;nbsp;currently&amp;nbsp;the&amp;nbsp;default\n&amp;nbsp;#&amp;nbsp;是否以安全认证方式运行，默认是不认证的非安全方式\n&amp;nbsp;#noauth&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#auth&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;Verbose&amp;nbsp;logging&amp;nbsp;output.\n&amp;nbsp;#&amp;nbsp;详细记录输出\n&amp;nbsp;#verbose&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;Inspect&amp;nbsp;all&amp;nbsp;client&amp;nbsp;data&amp;nbsp;for&amp;nbsp;validity&amp;nbsp;on&amp;nbsp;receipt&amp;nbsp;(useful&amp;nbsp;for\n&amp;nbsp;#&amp;nbsp;developing&amp;nbsp;drivers)用于开发驱动程序时的检查客户端接收数据的有效性\n&amp;nbsp;#objcheck&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;Enable&amp;nbsp;db&amp;nbsp;quota&amp;nbsp;management&amp;nbsp;启用数据库配额管理，默认每个db可以有8个文件，可以用quotaFiles参数设置\n&amp;nbsp;#quota&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;设置oplog记录等级\n&amp;nbsp;#&amp;nbsp;Set&amp;nbsp;oplogging&amp;nbsp;level&amp;nbsp;where&amp;nbsp;n&amp;nbsp;is\n&amp;nbsp;#&amp;nbsp;&amp;nbsp;&amp;nbsp;0=off&amp;nbsp;(default)\n&amp;nbsp;#&amp;nbsp;&amp;nbsp;&amp;nbsp;1=W\n&amp;nbsp;#&amp;nbsp;&amp;nbsp;&amp;nbsp;2=R\n&amp;nbsp;#&amp;nbsp;&amp;nbsp;&amp;nbsp;3=both\n&amp;nbsp;#&amp;nbsp;&amp;nbsp;&amp;nbsp;7=W+some&amp;nbsp;reads\n&amp;nbsp;#oplog&amp;nbsp;=&amp;nbsp;0\n&amp;nbsp;#&amp;nbsp;Diagnostic/debugging&amp;nbsp;option&amp;nbsp;动态调试项\n&amp;nbsp;#nocursors&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;Ignore&amp;nbsp;query&amp;nbsp;hints&amp;nbsp;忽略查询提示\n&amp;nbsp;#nohints&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;禁用http界面，默认为localhost：28017\n&amp;nbsp;#&amp;nbsp;Disable&amp;nbsp;the&amp;nbsp;HTTP&amp;nbsp;interface&amp;nbsp;(Defaults&amp;nbsp;to&amp;nbsp;localhost:27018).这个端口号写的是错的\n&amp;nbsp;#nohttpinterface&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;关闭服务器端脚本，这将极大的限制功能\n&amp;nbsp;#&amp;nbsp;Turns&amp;nbsp;off&amp;nbsp;server-side&amp;nbsp;scripting.&amp;nbsp;&amp;nbsp;This&amp;nbsp;will&amp;nbsp;result&amp;nbsp;in&amp;nbsp;greatly&amp;nbsp;limited\n&amp;nbsp;#&amp;nbsp;functionality\n&amp;nbsp;#noscripting&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;关闭扫描表，任何查询将会是扫描失败\n&amp;nbsp;#&amp;nbsp;Turns&amp;nbsp;off&amp;nbsp;table&amp;nbsp;scans.&amp;nbsp;&amp;nbsp;Any&amp;nbsp;query&amp;nbsp;that&amp;nbsp;would&amp;nbsp;do&amp;nbsp;a&amp;nbsp;table&amp;nbsp;scan&amp;nbsp;fails.\n&amp;nbsp;#notablescan&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;关闭数据文件预分配\n&amp;nbsp;#&amp;nbsp;Disable&amp;nbsp;data&amp;nbsp;file&amp;nbsp;preallocation.\n&amp;nbsp;#noprealloc&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#&amp;nbsp;为新数据库指定.ns文件的大小，单位:MB\n&amp;nbsp;#&amp;nbsp;Specify&amp;nbsp;.ns&amp;nbsp;file&amp;nbsp;size&amp;nbsp;for&amp;nbsp;new&amp;nbsp;databases.\n&amp;nbsp;#&amp;nbsp;nssize&amp;nbsp;=&amp;nbsp;;size;\n&amp;nbsp;#&amp;nbsp;Accout&amp;nbsp;token&amp;nbsp;for&amp;nbsp;Mongo&amp;nbsp;monitoring&amp;nbsp;server.\n&amp;nbsp;#mms-token&amp;nbsp;=&amp;nbsp;;token;\n&amp;nbsp;#&amp;nbsp;mongo监控服务器的名称\n&amp;nbsp;#&amp;nbsp;Server&amp;nbsp;name&amp;nbsp;for&amp;nbsp;Mongo&amp;nbsp;monitoring&amp;nbsp;server.\n&amp;nbsp;#mms-name&amp;nbsp;=&amp;nbsp;;server-name;\n&amp;nbsp;#&amp;nbsp;mongo监控服务器的ping&amp;nbsp;间隔\n&amp;nbsp;#&amp;nbsp;Ping&amp;nbsp;interval&amp;nbsp;for&amp;nbsp;Mongo&amp;nbsp;monitoring&amp;nbsp;server.\n&amp;nbsp;#mms-interval&amp;nbsp;=&amp;nbsp;;seconds;\n&amp;nbsp;#&amp;nbsp;Replication&amp;nbsp;Options&amp;nbsp;复制选项\n&amp;nbsp;#&amp;nbsp;in&amp;nbsp;replicated&amp;nbsp;mongo&amp;nbsp;databases,&amp;nbsp;specify&amp;nbsp;here&amp;nbsp;whether&amp;nbsp;this&amp;nbsp;is&amp;nbsp;a&amp;nbsp;slave&amp;nbsp;or&amp;nbsp;master&amp;nbsp;在复制中，指定当前是从属关系\n&amp;nbsp;#slave&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#source&amp;nbsp;=&amp;nbsp;master.example.com\n&amp;nbsp;#&amp;nbsp;Slave&amp;nbsp;only:&amp;nbsp;specify&amp;nbsp;a&amp;nbsp;single&amp;nbsp;database&amp;nbsp;to&amp;nbsp;replicate\n&amp;nbsp;#only&amp;nbsp;=&amp;nbsp;master.example.com\n&amp;nbsp;#&amp;nbsp;or\n&amp;nbsp;#master&amp;nbsp;=&amp;nbsp;true\n&amp;nbsp;#source&amp;nbsp;=&amp;nbsp;slave.example.com&lt;/pre&gt;&lt;br/&gt;&lt;p&gt;&lt;strong&gt;转载请保留固定链接： &lt;a href=&quot;http://www.linuxeye.com/configuration/1747.html&quot;&gt;http://www.linuxeye.com/configuration/1747.html&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 5, 1, 1, 1445225673),
	(31, 18, 7, 1, 'linux搭建svn服务器端和客户端说明', '&lt;p&gt;安装包获取&lt;br/&gt;wget http://subversion.tigris.org/downloads/subversion-1.6.6.tar.gz&lt;br/&gt;wget http://subversion.tigris.org/downloads/subversion-deps-1.6.6.tar.gz &lt;br/&gt;&lt;br/&gt;解压文件（务必将两个文件解压到相同的目录下，并且版本要一致）&lt;br/&gt;tar zxf subversion-1.6.6.tar.gz &lt;br/&gt;tar zxf subversion-deps-1.6.6.tar.gz &amp;nbsp;&lt;br/&gt;&lt;br/&gt;进入解压后的subversion目录&lt;br/&gt;cd /xxxxxx/subversion-1.6.6/&lt;br/&gt;&lt;br/&gt;创建安装目录&lt;br/&gt;mkdir /usr/local/svn&lt;br/&gt;&lt;br/&gt;编译安装&lt;br/&gt;./configure --prefix=/usr/local/svn&lt;br/&gt;make&lt;br/&gt;make install&lt;br/&gt;【如果出现错误：configure: error: no suitable apr found 则是因为上面的依赖文件没有减压到相同目录下导致。。。】&lt;br/&gt;&lt;br/&gt;查看版本&lt;br/&gt;/usr/local/svn/bin/svnserve --version&lt;br/&gt;&lt;br/&gt;建立测试仓库&lt;br/&gt;1.建立SVN的根目录，因为通常都有多个项目&lt;br/&gt;mkdir -p&amp;nbsp;/data0/svn/&lt;br/&gt;2.建立一个测试仓库&lt;br/&gt;mkdir -p&amp;nbsp;/data0/svn/svntest/&lt;br/&gt;svnadmin create /data0/svn/svntest/ 3.配置&lt;br/&gt;&lt;br/&gt;cd /data0/svn/svntest/conf/&lt;br/&gt;vi svnserve.conf&lt;br/&gt;[general]&lt;br/&gt;anon-access = none&lt;br/&gt;auth-access = write&lt;br/&gt;password-db = passwd&lt;br/&gt;authz-db = authz&lt;br/&gt;&lt;br/&gt;vi authz&lt;br/&gt;[svntest:/]&lt;br/&gt;xiaocai = rw&lt;br/&gt;#给svntest仓库添加一个xiaocai用户,权限为可写&lt;br/&gt;&lt;br/&gt;vi passwd&lt;br/&gt;xiaocai = 123456&lt;br/&gt;&lt;br/&gt;#设置用户密码&lt;br/&gt;启动svn服务,指定SVN的根目录：&lt;br/&gt;svnserve -d&amp;nbsp;-r&amp;nbsp;/data0/svn/&lt;br/&gt;【&lt;br/&gt;安装时遇到的问题: &lt;br/&gt;启动svnserve时出现cannot bind server socket:address already in use &lt;br/&gt;解决方法: &lt;br/&gt;方法一:找出svnserve id 杀死掉进程 &lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; netstat -ntlp &lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; kill -9&amp;nbsp;{PID号} &lt;br/&gt;方法二:重新指定一个端口号(默认端口3690) &lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; svnserve -d&amp;nbsp;--listen-port 3691 -r&amp;nbsp;/opt/svndata/svnrepo&lt;br/&gt;】&lt;br/&gt;&lt;br/&gt;查看是否启用&lt;br/&gt;netstat -tunlp&amp;nbsp;| grep svn&lt;br/&gt;tcp &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 0 &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 0 0.0.0.0:3690 &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 0.0.0.0:*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; LISTEN &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 8646/svnserve&lt;br/&gt;&lt;br/&gt;提交后更新到web目录下&lt;br/&gt;cd /var/www/&lt;br/&gt;svn checkout svn://42.121.4.220/www.icosplay.cc/&lt;br/&gt;&lt;br/&gt;钩子&lt;br/&gt;vi /data0/svn/hooks/post-commit&lt;br/&gt;&lt;br/&gt;#!/bin/sh&lt;br/&gt;REPOS=&amp;quot;$1&amp;quot;&lt;br/&gt;REV=&amp;quot;$2&amp;quot;&lt;br/&gt;svn update /mnt/www/www.icosplay.cc&amp;nbsp;--username xiaocai --password 123123&lt;br/&gt;&lt;br/&gt;Warning: post-commit hook failed (exit code 255) with no output.&lt;br/&gt;如果执行提交时SVN提示255错误，则是 post-commit 脚本文件的权限不对，post-commit&amp;nbsp;脚本必须有&amp;nbsp;+x&amp;nbsp;权限。&lt;br/&gt;&lt;br/&gt;chown svn:svn post-commit&lt;br/&gt;chmod +x post-commit&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;问题汇总：&lt;br/&gt;1.configure: error: no XML parser was found: expat or libxml 2.x required&lt;br/&gt;[plain] view plaincopyprint?&lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;错误提示需要安装expat（下载expat2.tar.gz）&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;cd&amp;nbsp;/usr/local&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;tar&amp;nbsp;-xvzf&amp;nbsp;expat2.tar.gz&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;cd&amp;nbsp;expat2&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;./configure&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp;install&amp;nbsp; &lt;br/&gt;2.configure: error: We require OpenSSL; try --with-openssl&lt;br/&gt;[plain] view plaincopyprint?&lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;错误提示需要安装openssl，所以我就安装了一个openssl，安装方法如下：&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;cd&amp;nbsp;/usr/local&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;tar&amp;nbsp;-zxvf&amp;nbsp;openssl-1.0.0a.tar.gz&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;cd&amp;nbsp;openssl-1.0.0a&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;./config&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;./config&amp;nbsp;-t&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp;depend&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp;test&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp;instal&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;安装之后会在/usr/local下生成一个ssl目录，配置SVN时加上openssl路径&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;./configure&amp;nbsp;--prefix=/home/svnadmin/subversion&amp;nbsp;--without-berkeley-db&amp;nbsp;--with-openssl=/usr/local/ssl&amp;nbsp;&amp;nbsp; &lt;br/&gt;3.configure: error: subversion requires zlib&lt;br/&gt;[plain] view plaincopyprint?&lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;错误提示需要安装zlib&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;下载zlb：http://zlib.net/&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;cd&amp;nbsp;/usr/local&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;tar&amp;nbsp;-xvzf&amp;nbsp;zlib-1.2.5.tar.gz&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;cd&amp;nbsp;zlib-1.2.5&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;./configure&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;make&amp;nbsp;install&amp;nbsp; &lt;br/&gt;4.svn checkout https://xxx.xxx.xxx/&amp;nbsp; &lt;br/&gt;[plain] view plaincopyprint?&lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;svn:&amp;nbsp;SSL&amp;nbsp;is&amp;nbsp;not&amp;nbsp;supported&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;从网上查了一下，说是在执行./configure&amp;nbsp;时要加上--with-ssl&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;•&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;./configure&amp;nbsp;--with-openssl=/usr/local/ssl&amp;nbsp;--with-zlib=/usr/local/zlib&amp;nbsp;--with-ssl&amp;nbsp;--with-libs=/usr/local/ssl &lt;br/&gt;&lt;/p&gt;&lt;!--默认内容在这里 --&gt;&lt;div id=&quot;xunlei_com_thunder_helper_plugin_d462f475-c18e-46be-bd10-327458d045bd&quot;&gt;&lt;/div&gt;', 9, 1, 1, 1445256803),
	(32, 18, 12, 1, '网址大全', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;windows系统：&lt;a href=&quot;http://msdn.itellyou.cn/&quot; target=&quot;_self&quot;&gt;http://msdn.itellyou.cn/&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;git资料：&lt;a href=&quot;http://www.open-open.com/lib/view/open1328069609436.html#_label2&quot; target=&quot;_self&quot;&gt;http://www.open-open.com/lib/view/open1328069609436.html#_label2&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px; font-family: arial,helvetica,sans-serif;&quot;&gt;中国首个开源 HTML5 跨屏前端框架&lt;/span&gt;:&lt;a href=&quot;http://amazeui.org/&quot; target=&quot;_self&quot;&gt;http://amazeui.org/&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;百度前端开发：&lt;a href=&quot;http://fex.baidu.com/&quot; target=&quot;_self&quot;&gt;http://fex.baidu.com/&lt;/a&gt;&lt;/p&gt;&lt;p&gt;上传图片：&lt;a href=&quot;http://fex.baidu.com/webuploader/&quot; target=&quot;_self&quot;&gt;http://fex.baidu.com/webuploader/&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;前端开发：&lt;a href=&quot;http://www.htmleaf.com/&quot; target=&quot;_self&quot;&gt;http://www.htmleaf.com/&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;redis:&lt;a href=&quot;http://blog.nosqlfan.com/html/2235.html&quot; target=&quot;_self&quot;&gt;http://blog.nosqlfan.com/html/2235.html&lt;/a&gt;&lt;/p&gt;&lt;p&gt;redis命令：&lt;a href=&quot;http://redis.io/commands&quot; target=&quot;_self&quot;&gt;http://redis.io/commands&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;Google 镜像站搜集&amp;nbsp;：h&lt;a href=&quot;http://ttp://www.itechzero.com/google-mirror-sites-collect.html&quot; target=&quot;_self&quot;&gt;ttp://www.itechzero.com/google-mirror-sites-collect.html&amp;nbsp;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;原版网页：&lt;a href=&quot;https://www.guge.link/&quot; _src=&quot;https://www.guge.link/&quot;&gt;https://www.guge.link/&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;谷歌图片：&lt;a href=&quot;https://googo.pw/imghp&quot; _src=&quot;https://googo.pw/imghp&quot;&gt;https://googo.pw/imghp&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;运维网站：&lt;a href=&quot;https://linux.cn/&quot; _src=&quot;https://linux.cn&quot; style=&quot;white-space: normal;&quot;&gt;https://linux.cn&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;Linux命令大全：&lt;a href=&quot;http://man.linuxde.net/&quot; _src=&quot;http://man.linuxde.net/&quot; style=&quot;white-space: normal;&quot;&gt;http://man.linuxde.net/&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;分享母婴儿童早教经验_提供动漫资源下载的育儿网站：&lt;a href=&quot;http://www.mamabikan.com/&quot; target=&quot;_self&quot; style=&quot;white-space: normal;&quot;&gt;http://www.mamabikan.com/&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;一个神奇的网盘搜索：&lt;a target=&quot;_self&quot; href=&quot;http://www.panzz.com/&quot; style=&quot;white-space: normal;&quot;&gt;http://www.panzz.com/&lt;/a&gt;&amp;nbsp;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;画图工具：&lt;a href=&quot;https://www.processon.com/&quot; _src=&quot;https://www.processon.com/&quot; style=&quot;white-space: normal;&quot;&gt;https://www.processon.com/&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;模拟get/post请求firefox插件：hackbar&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;压力测试工具：jmeter&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;web开发：&lt;a href=&quot;http://www.runoob.com/&quot; target=&quot;_self&quot;&gt;http://www.runoob.com/&amp;nbsp;&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;a href=&quot;http://www.runoob.com/&quot; target=&quot;_self&quot;&gt;&lt;/a&gt;精品mac应用：http://xclient.info/&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;翻墙：https://aiguge.xyz/shadowsocks-download/&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;数据库框架：&lt;a href=&quot;http://medoo.lvtao.net/&quot; target=&quot;_self&quot;&gt;http://medoo.lvtao.net/&lt;/a&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2次方：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;机器码+编号：62183-00000-54328-00000+php_39&lt;/p&gt;&lt;p&gt;&amp;nbsp;用户名密码：584801895 /&amp;nbsp;yll03bk7uq8lxl9b&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;老男孩：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;1.&lt;a class=&quot;breadcrumb-item item--tt&quot; href=&quot;http://ke.qq.com/course/109101&quot;&gt;老男孩教育2016年第一期免费系列课程&lt;/a&gt;：&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://ke.qq.com/course/109101#term_id=100121544&amp;&amp;&amp;&amp;&amp;ADSESSION=1453174947&amp;ADTAG=CLIENT.QQ.5456_.0&amp;ADPUBNO=26542&quot; _src=&quot;http://ke.qq.com/course/109101#term_id=100121544&amp;&amp;&amp;&amp;&amp;ADSESSION=1453174947&amp;ADTAG=CLIENT.QQ.5456_.0&amp;ADPUBNO=26542&quot;&gt;http://ke.qq.com/course/109101#term_id=100121544&amp;amp;&amp;amp;&amp;amp;&amp;amp;&amp;amp;ADSESSION=1453174947&amp;amp;ADTAG=CLIENT.QQ.5456_.0&amp;amp;ADPUBNO=26542&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;2.&lt;a href=&quot;http://blog.oldboyedu.com/&quot; target=&quot;_self&quot;&gt;http://blog.oldboyedu.com/&lt;/a&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;', 48, 1, 2, 1445259952),
	(33, 18, 13, 1, '让Mac OS X系统启动时执行脚本 ', '&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;公司购买了Zoho公司的资产管理软件\nAssetExplorer，该资产管理软件可以通过在客户机电脑上安装Agent客户端软件收集客户机的硬件及软件信息。公司有若干台iMac电脑，运\n行的是Mac OS X \n10.6的系统，由于AssetExplorer只支持Windows版本的Agent，对于其他系统的计算机需要通过在客户端执行bash脚本文件的方\n式，收集客户机的信息并上传到服务器。这就要求客户机的Mac OS \nX系统启动的时候能够自动执行脚本程序，而且要基于系统服务层面，用户无需登录，脚本也能在后台运行，最好脚本程序后台还能做到固定时间自动运行。&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;\n &amp;nbsp;我们可以利用Mac OS X内置的一种称为Launch Daemon / Agent的机制来实现系统启动时自动执行脚本程序。Mac OS \nX从10.4 开始，采用 launchd进程来管理整个操作系统的服务及进程。传统的UNIX 会使用 /etc/rc.* \n或其他的机制来管理开机时要启动的启动服务，而现在的Mac OS X则使用launchd来管理，它的启动服务称为Launch Daemon / \nAgents。利用Launch Daemon / Agent，我们就可以令脚本程序在系统启动的时候在后台运行了。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;ul class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: disc;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;Launch Daemon和Launch Agent的区别&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;　\n　Launch Daemon和Launch Agent 是同一种东西在不同应用范围的名称。Launch \nDaemon是系统级别的服务，称为daemon，Launch \nAgent是用户级别的服务，称为agent，前者在开机时会加载，后者在用户登录后才会加载。所以我们采用Launch Daemon的方式运行脚本。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;ul class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: disc;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;从AssetExolorer导出脚本文件&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;　　选择扫描→孤立工作站审计→通过脚本扫描工作站→Mac导出脚本文件ae_scan_mac.sh，并修改脚本文件中的hostName=&amp;quot;服务器IP地址或域名地址&amp;quot;，并检查portNo一项是否正确。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;ul class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: disc;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;创建plist配置文件&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;　\n　Launchd进程需要读取一个xml格式的plist配置文件，创建该文件并将其放置在/System/Library/LaunchDaemons\n/ \n或者/Library/LaunchDaemons/目录下。可在Windows下预先创建该文件，需要使用支持Unix格式的文本编辑器（比如\nUltraEdit）创建该文件。&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;　　&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;其中plist文件格式及每个字段的含义：&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;ae_scan_mac.plist文件范例：&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height:150%;text-indent:28px;&quot;&gt;&lt;span style=&quot;line-height:150%;font-family:&amp;#39;宋体&amp;#39;;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;div&gt;&lt;div id=&quot;highlighter_115046&quot; class=&quot;syntaxhighlighter  xml&quot;&gt;&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td class=&quot;gutter&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;1&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;2&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;3&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;4&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;5&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;6&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;7&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;8&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;9&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;10&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;11&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;12&lt;/div&gt;&lt;div class=&quot;line number13 index12 alt2&quot;&gt;13&lt;/div&gt;&lt;div class=&quot;line number14 index13 alt1&quot;&gt;14&lt;/div&gt;&lt;div class=&quot;line number15 index14 alt2&quot;&gt;15&lt;/div&gt;&lt;div class=&quot;line number16 index15 alt1&quot;&gt;16&lt;/div&gt;&lt;div class=&quot;line number17 index16 alt2&quot;&gt;17&lt;/div&gt;&lt;div class=&quot;line number18 index17 alt1&quot;&gt;18&lt;/div&gt;&lt;div class=&quot;line number19 index18 alt2&quot;&gt;19&lt;/div&gt;&lt;div class=&quot;line number20 index19 alt1&quot;&gt;20&lt;/div&gt;&lt;div class=&quot;line number21 index20 alt2&quot;&gt;21&lt;/div&gt;&lt;div class=&quot;line number22 index21 alt1&quot;&gt;22&lt;/div&gt;&lt;div class=&quot;line number23 index22 alt2&quot;&gt;23&lt;/div&gt;&lt;div class=&quot;line number24 index23 alt1&quot;&gt;24&lt;/div&gt;&lt;/td&gt;&lt;td class=&quot;code&quot;&gt;&lt;div class=&quot;container&quot;&gt;&lt;div class=&quot;line number1 index0 alt2&quot;&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;?&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;xml&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;xml color1&quot;&gt;version&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;=&lt;/code&gt;&lt;code class=&quot;xml string&quot;&gt;&amp;quot;1.0&amp;quot;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;xml color1&quot;&gt;encoding&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;=&lt;/code&gt;&lt;code class=&quot;xml string&quot;&gt;&amp;quot;UTF-8&amp;quot;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;?&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot;&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;!DOCTYPE&amp;nbsp;plist&amp;nbsp;PUBLIC&amp;nbsp;&amp;quot;-//Apple&amp;nbsp;Computer//DTD&amp;nbsp;PLIST&amp;nbsp;1.0//EN&amp;quot;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number3 index2 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;quot;http://www.apple.com/DTDs/PropertyList-1.0.dtd&amp;quot;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number4 index3 alt1&quot;&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;plist&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;xml color1&quot;&gt;version&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;=&lt;/code&gt;&lt;code class=&quot;xml string&quot;&gt;&amp;quot;1.0&amp;quot;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number5 index4 alt2&quot;&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;dict&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number6 index5 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;Label&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number7 index6 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;string&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;com.manageengine.ae_scan_mac&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;string&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number8 index7 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;Program&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number9 index8 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;string&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;ae_scan_mac.sh&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;string&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number10 index9 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;RunAtLoad&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number11 index10 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;true&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;/&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number12 index11 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;WorkingDirectory&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number13 index12 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;string&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;/Library/LaunchDaemons/&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;string&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number14 index13 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;KeepAlive&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number15 index14 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;false&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;/&amp;gt;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number16 index15 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;StartCalendarInterval&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number17 index16 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;dict&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number18 index17 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;Hour&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number19 index18 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;integer&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;11&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;integer&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number20 index19 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;Minute&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;key&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number21 index20 alt2&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;integer&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;0&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;integer&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number22 index21 alt1&quot;&gt;&lt;code class=&quot;xml spaces&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;dict&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&amp;nbsp;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number23 index22 alt2&quot;&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;dict&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number24 index23 alt1&quot;&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;lt;/&lt;/code&gt;&lt;code class=&quot;xml keyword&quot;&gt;plist&lt;/code&gt;&lt;code class=&quot;xml plain&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/div&gt;&lt;/div&gt;&lt;p&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height:150%;text-indent:28px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;宋体&amp;#39;, SimSun;&quot;&gt;其中的关键字解释：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;1）&lt;strong&gt;Label（必选）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;该项服务的名称。&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;2）&lt;strong&gt;Program&lt;/strong&gt;（Program&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;Argument是必选的，在没有ProgramArgument的情况下，必须要包含Program关键字）&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;指定可执行文件的路径和名称。&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;3）&lt;strong&gt;RunAtLoad（可选）&lt;/strong&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;标识launchd在加载完该项服务之后立即启动路径指定的可执行文件。默认值为false。设置为true即可实现开机运行脚本文件。&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;4）&lt;strong&gt;WorkingDirectory（可选）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;运行可执行文件之前，指定当前工作目录的路径。&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;5）&lt;strong&gt;KeepAlive（可选）&lt;/strong&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;这个关键字可以用来控制是否让可执行文件持续运行，默认值为false，也就是说满足具体条件之后才启动。当设置值为ture时，表明无条件的开启可执行文件，并使之保持在整个系统运行周期内。&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;6）&lt;strong&gt;StartCalendarInterval（可选）&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;该\n关键字可以用来设置定时执行可执行程序，可使用Month、Day、Hour、Minute等子关键字，它可以指定脚本在多少月、天、小时、分钟、星期几\n等时间上执行，若缺少某个关键字则表示任意该时间点，类似于Unix的Crontab计划任务的设置方式，比如在该例子中设置为每天11点钟执行脚本文\n件。&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;所有key关键字详细使用说明可以在Mac OS X终端下使用命令man launchd.plist查询。&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;line-height:0px;font-family:&amp;#39;宋体&amp;#39;, SimSun;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;ul class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: disc;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;strong&gt;在客户机部署配置文件和脚本文件&lt;/strong&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;1） 首先为Mac OS X系统的当前的用户设置一个密码，在系统偏好设置→账户中设置。&lt;/p&gt;&lt;p&gt;　　然后，可通过共享等方式将脚本文件ae_scan_mac.sh和plist文件ae_scan_mac.plist拷贝到本地硬盘的一个临时目录中，然后打开实用工具→终端，使用以下命令：&lt;/p&gt;&lt;p&gt;sudo mv ae_scan_mac.* /Library/LaunchDaemons&lt;/p&gt;&lt;p&gt;将这两个文件移动到/Library/LaunchDaemons/目录下。&lt;/p&gt;&lt;p style=&quot;line-height:150%;text-indent:0px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;2） 修改文件权限：&lt;/p&gt;&lt;p&gt;sudo chown root:wheel /Library/LaunchDaemons/ae_scan_mac.*&lt;/p&gt;&lt;p&gt;sudo chmod a+x /Library/LaunchDaemons/ae_scan_mac.sh&lt;/p&gt;&lt;p&gt;sudo chmod 644 /Library/LaunchDaemons/ae_scan_mac.plist&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;line-height:150%;&quot;&gt;&lt;span style=&quot;line-height:150%;font-size:16px;&quot;&gt;&lt;a href=&quot;http://s3.51cto.com/wyfs02/M02/73/F1/wKiom1YKR_WCHjOwAAJa6EWwXoQ081.jpg&quot; target=&quot;_blank&quot;&gt;&lt;img title=&quot;change file permissions.png&quot; alt=&quot;wKiom1YKR_WCHjOwAAJa6EWwXoQ081.jpg&quot; src=&quot;http://s3.51cto.com/wyfs02/M02/73/F1/wKiom1YKR_WCHjOwAAJa6EWwXoQ081.jpg&quot;/&gt;&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;3） plist文件的编辑和检查&lt;/p&gt;&lt;p&gt;可使用命令sudo vim ae_scan_mac.plist对文件进行修改。&lt;/p&gt;&lt;p&gt;检查plist文件的语法：&lt;/p&gt;&lt;p&gt;plutil ae_scan_mac.plist&lt;/p&gt;&lt;p&gt;若返回OK则表示文件语法正确。&lt;/p&gt;&lt;p style=&quot;line-height:150%;text-indent:0px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;4） 启动服务并载入plsit文件：&lt;/p&gt;&lt;p&gt;sudo launchctl load /Library/LaunchDaemons/ae_scan_mac.plist&lt;/p&gt;&lt;p&gt;或者重启Mac电脑也可以。&lt;/p&gt;&lt;p&gt;要留意的是，每次修改完plist文件后必须使用以下命令重新载入plist文件又或者重启电脑，plist的配置才会生效。&lt;/p&gt;&lt;p&gt;sudo launchctl unload /Library/LaunchDaemons/ae_scan_mac.plist&lt;/p&gt;&lt;p&gt;sudo launchctl load /Library/LaunchDaemons/ae_scan_mac.plist&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;接着，检查服务的运行状态：&lt;/p&gt;&lt;p&gt;sudo launchctl list&lt;/p&gt;&lt;p style=&quot;line-height:150%;text-indent:0px;&quot;&gt;&lt;a href=&quot;http://s3.51cto.com/wyfs02/M01/73/EE/wKioL1YKSLCy3gpUAAIkefzxnxA635.jpg&quot; target=&quot;_blank&quot;&gt;&lt;img title=&quot;launchctl list.png&quot; alt=&quot;wKioL1YKSLCy3gpUAAIkefzxnxA635.jpg&quot; src=&quot;http://s3.51cto.com/wyfs02/M01/73/EE/wKioL1YKSLCy3gpUAAIkefzxnxA635.jpg&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;5） 最后待脚本运行后，就能在AssetExplorer服务器上看到这台客户机的信息了，之后客户机就会自动按照plist文件设置的时间或者重启机器后将机器信息上传到服务器。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;两个文件的部署有几个地方需要注意的：&lt;br/&gt;&lt;/p&gt;&lt;p&gt;1）文件的权限必须正确，两个文件所有者必须为root，脚本文件必须有执行权限。&lt;/p&gt;&lt;p&gt;2）plist文件的语法必须正确，要留意配置中的程序工作目录和运行参数不要错。可执行：sudo launchctl start /Library/LaunchDaemons/ae_scan_mac.plist&lt;/p&gt;&lt;p&gt;强制运行服务，或者直接运行一下脚本程序，测试一下脚本文件的运行是否正常。&lt;/p&gt;&lt;p&gt;3）每次plist文件修改后必须执行launchctl unload/load卸载并重新载入plist文件，才能使新的配置文件马上生效。&lt;/p&gt;&lt;p&gt;　\n　脚本程序和配置文件分别在多台客户机一次性部署后，AssetExplorer就可以自动收集到所有Mac电脑的硬件和软件信息，即使机器配置更改了也\n无需手动添加，方便了公司的资产管理。Launchd进程的功能比较强大，Apple推荐并已使用该进程替代传统的cron了，大家有兴趣可以再研究一\n下。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;转载自：http://marcohwt.blog.51cto.com/804927/1699196&lt;/p&gt;&lt;!--默认内容在这里 --&gt;&lt;div id=&quot;xunlei_com_thunder_helper_plugin_d462f475-c18e-46be-bd10-327458d045bd&quot;&gt;&lt;/div&gt;', 3, 1, 1, 1445658277),
	(34, 18, 14, 1, 'Vi 文本编辑器', '&lt;h1&gt;1.Vi&lt;span style=&quot;font-family:宋体&quot;&gt;文本编辑器的三种模式：&lt;/span&gt;&lt;/h1&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Times New Roman&amp;#39;,&amp;#39;serif&amp;#39;&quot;&gt;&lt;img src=&quot;/Uploads/ueditor/php/upload/image/20160306/1457262728558563.png&quot; title=&quot;1457262728558563.png&quot; alt=&quot;blob.png&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;2.插入命令：&lt;/span&gt; &lt;/h1&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot; style=&quot;;height:36px&quot;&gt;&lt;td style=&quot;border: 1px solid windowtext; padding: 0px 7px; word-break: break-all;&quot; height=&quot;36&quot; width=&quot;130&quot;&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;命令&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-width: 1px 1px 1px medium; border-style: solid solid solid none; border-color: windowtext windowtext windowtext -moz-use-text-color; -moz-border-top-colors: none; -moz-border-right-colors: none; -moz-border-bottom-colors: none; -moz-border-left-colors: none; border-image: none; padding: 0px 7px; word-break: break-all;&quot; height=&quot;36&quot; width=&quot;254&quot;&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;作用&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;;height:36px&quot;&gt;&lt;td style=&quot;border-width: medium 1px 1px; border-style: none solid solid; border-color: -moz-use-text-color windowtext windowtext; -moz-border-top-colors: none; -moz-border-right-colors: none; -moz-border-bottom-colors: none; -moz-border-left-colors: none; border-image: none; padding: 0px 7px;&quot; height=&quot;36&quot; width=&quot;130&quot;&gt;&lt;p style=&quot;text-align:center&quot;&gt;a&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-width: med