amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_Log_File:&amp;nbsp;mysql-bin.000002\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Read_Master_Log_Pos:&amp;nbsp;419\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Relay_Log_File:&amp;nbsp;relay-bin.000003\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Relay_Log_Pos:&amp;nbsp;253\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Relay_Master_Log_File:&amp;nbsp;mysql-bin.000002\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Slave_IO_Running:&amp;nbsp;Yes&amp;nbsp;&amp;nbsp;&amp;nbsp;//如果此处出现两yes,说明同步成功\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Slave_SQL_Running:&amp;nbsp;Yes\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Replicate_Do_DB:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Replicate_Ignore_DB:&amp;nbsp;mysql\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Replicate_Do_Table:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Replicate_Ignore_Table:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Replicate_Wild_Do_Table:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Replicate_Wild_Ignore_Table:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Last_Errno:&amp;nbsp;0\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Last_Error:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Skip_Counter:&amp;nbsp;0\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Exec_Master_Log_Pos:&amp;nbsp;419\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Relay_Log_Space:&amp;nbsp;403\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Until_Condition:&amp;nbsp;None\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Until_Log_File:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Until_Log_Pos:&amp;nbsp;0\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_SSL_Allowed:&amp;nbsp;No\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_SSL_CA_File:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_SSL_CA_Path:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_SSL_Cert:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_SSL_Cipher:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_SSL_Key:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Seconds_Behind_Master:&amp;nbsp;0\n&amp;nbsp;&amp;nbsp;Master_SSL_Verify_Server_Cert:&amp;nbsp;No\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Last_IO_Errno:&amp;nbsp;0\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Last_IO_Error:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Last_SQL_Errno:&amp;nbsp;0\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Last_SQL_Error:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Replicate_Ignore_Server_Ids:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_Server_Id:&amp;nbsp;1\n&amp;nbsp;&amp;nbsp;1&amp;nbsp;row&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.00&amp;nbsp;sec)\n\n&amp;nbsp;&amp;nbsp;ERROR:&amp;nbsp;\n&amp;nbsp;&amp;nbsp;No&amp;nbsp;query&amp;nbsp;specified\n\n&amp;nbsp;&amp;nbsp;mysql&amp;gt;&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; ==================================================================&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp; 故障解决&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; 1.主库show master status 没有结果&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;原因：主库的binlog功能开关没开或没有生效&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;mysql&amp;gt; &amp;nbsp;show variables like &amp;#39;log_bin&amp;#39;;&lt;/p&gt;&lt;p&gt;&amp;nbsp; +---------------+-------+&lt;/p&gt;&lt;p&gt;&amp;nbsp; | Variable_name | Value |&lt;/p&gt;&lt;p&gt;&amp;nbsp; +---------------+-------+&lt;/p&gt;&lt;p&gt;&amp;nbsp; | log_bin &amp;nbsp; &amp;nbsp; &amp;nbsp; | ON &amp;nbsp; &amp;nbsp;|&lt;/p&gt;&lt;p&gt;&amp;nbsp; +---------------+-------+&lt;/p&gt;&lt;p&gt;&amp;nbsp; 1 row in set (0.00 sec)&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; mysql&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; 2.服务不能启动&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;删掉pid和sock，再重新启动&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp; 总结MySQL主从复制的步骤：&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;1.准备两台数据库环境，或者单台多实例环境，能否正常启动和登录&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;2.配置my.cnf文件，主库配置log-bin和server-id参数，从库配置server-id,不能和主库及其他从库一样，一般不开启从库log-bin功能。&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;注意：配置参数后要重启生效&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;3.登录主库增加用于从库连接主库同步的账户，例如：rep,并授权replication同步的权限&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;4.登录主库，整库锁表flush table with read lock(窗口关闭后即失效，超时参数到了也失效)；然后show master status 查看binlog的位置状态&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;5.新开窗口，linux命令行备份或者导出原有的数据库数据，并拷贝到从库所在的服务器目录&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;如果数据量很大，并且允许停机，而不用mysqldump&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;6.解锁主库，unlock tables;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;7. 把主库导出的原有数据恢复到从库&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;8.根据主库的show master status 查看binlog 的位置状态，在从库执行change master to ...语句&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;9.从库开启同步开关，start slave&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;10.从库show slave status\\G,查看同步状态，并在主库进行更新测试&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3.MySQL双主复制 3306,&amp;lt;==&amp;gt;3307&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; 3.1.双主的实现方案：&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; a.让表的ID自增，让后主1 &amp;nbsp;写1、3、5 &amp;nbsp; &amp;nbsp;主2写2 4 6....&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; b.不让表的ID自增&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; 3.2.在3306上添加如下行&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;#________m-m&amp;nbsp;m1&amp;nbsp;start________&amp;nbsp;&amp;nbsp;auto_increment_increment&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;2&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#自动增加的间隔为2\n&amp;nbsp;&amp;nbsp;auto_increment_offset&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;1&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#自动增加的其实位置为1\n&amp;nbsp;&amp;nbsp;log-slave-updates&amp;nbsp;&amp;nbsp;\n&amp;nbsp;&amp;nbsp;expire_logs_days&amp;nbsp;=&amp;nbsp;7&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#这一行之前就有，保留七天的mysql-bin日志\n&amp;nbsp;&amp;nbsp;log-bin&amp;nbsp;=&amp;nbsp;/data/3306/mysql-bin&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#这一行之前就有\n&amp;nbsp;&amp;nbsp;#________m-m&amp;nbsp;m1&amp;nbsp;end________&lt;/pre&gt;&lt;p&gt;&amp;nbsp; 3.3.在3307上添加如下几行&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;#________m-m&amp;nbsp;m2&amp;nbsp;start________&amp;nbsp;&amp;nbsp;auto_increment_increment&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;2\n&amp;nbsp;&amp;nbsp;auto_increment_offset&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;2\n&amp;nbsp;&amp;nbsp;log-slave-updates\n&amp;nbsp;&amp;nbsp;expire_logs_days&amp;nbsp;=&amp;nbsp;7\n&amp;nbsp;&amp;nbsp;log-bin&amp;nbsp;=&amp;nbsp;/data/3307/mysql-bin\n&amp;nbsp;&amp;nbsp;#________m-m&amp;nbsp;m2&amp;nbsp;end________&lt;/pre&gt;&lt;p&gt;&amp;nbsp; 3.4.重启数据库&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;[root@oldboy&amp;nbsp;3306]#&amp;nbsp;./mysql&amp;nbsp;stop&amp;nbsp;&amp;nbsp;Stoping&amp;nbsp;MySQL...\n&amp;nbsp;&amp;nbsp;[root@oldboy&amp;nbsp;3306]#&amp;nbsp;./mysql&amp;nbsp;start\n&amp;nbsp;&amp;nbsp;Starting&amp;nbsp;MySQL...\n&amp;nbsp;&amp;nbsp;[root@oldboy&amp;nbsp;3306]#&amp;nbsp;\n&amp;nbsp;&amp;nbsp;[root@oldboy&amp;nbsp;3307]#&amp;nbsp;./mysql&amp;nbsp;stop\n&amp;nbsp;&amp;nbsp;Stoping&amp;nbsp;MySQL...\n&amp;nbsp;&amp;nbsp;[root@oldboy&amp;nbsp;3307]#&amp;nbsp;./mysql&amp;nbsp;start\n&amp;nbsp;&amp;nbsp;Starting&amp;nbsp;MySQL...&lt;/pre&gt;&lt;p&gt;&amp;nbsp; 3.5.在3307上备份全库&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;mysqldump&amp;nbsp;-uroot&amp;nbsp;-pold123&amp;nbsp;&amp;nbsp;-S&amp;nbsp;/data/3307/mysql.sock&amp;nbsp;-A&amp;nbsp;-B&amp;nbsp;--events&amp;nbsp;-x&amp;nbsp;--master-data=1|gzip&amp;nbsp;&amp;gt;/opt/mysql3307_$(date&amp;nbsp;+%F).sql.gz&lt;/pre&gt;&lt;p&gt;&amp;nbsp; 3.6.在3306主库上导入在3307主库上备份的全库&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;gzip&amp;nbsp;-d&amp;nbsp;mysql3307_2015-11-12.sql.gz&amp;nbsp;&amp;nbsp;mysql&amp;nbsp;-uroot&amp;nbsp;-p123456&amp;nbsp;-S&amp;nbsp;/data/3307/mysql.sock&amp;nbsp;&amp;lt;&amp;nbsp;mysql3307_2015-11-12.sql\n&amp;nbsp;&amp;nbsp;mysql&amp;nbsp;-uroot&amp;nbsp;-poldboy123&amp;nbsp;-S&amp;nbsp;/data/3307/mysql.sock&amp;lt;&amp;lt;EOF\n&amp;nbsp;&amp;nbsp;CHANGE&amp;nbsp;MASTER&amp;nbsp;TO&amp;nbsp;&amp;nbsp;\n&amp;nbsp;&amp;nbsp;MASTER_HOST=&amp;#39;192.168.76.132&amp;#39;,\n&amp;nbsp;&amp;nbsp;MASTER_PORT=3306,\n&amp;nbsp;&amp;nbsp;MASTER_USER=&amp;#39;rep&amp;#39;,\n&amp;nbsp;&amp;nbsp;MASTER_PASSWORD=&amp;#39;oldboy123&amp;#39;;\n&amp;nbsp;&amp;nbsp;EOF&lt;/pre&gt;&lt;p&gt;&amp;nbsp; 3.7.3306上开启同步开关&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;mysql&amp;gt;&amp;nbsp;start&amp;nbsp;slave;&amp;nbsp;&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.00&amp;nbsp;sec)\n\n&amp;nbsp;&amp;nbsp;mysql&amp;gt;&amp;nbsp;show&amp;nbsp;slave&amp;nbsp;status\\G;\n&amp;nbsp;&amp;nbsp;***************************&amp;nbsp;1.&amp;nbsp;row&amp;nbsp;***************************\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Slave_IO_State:&amp;nbsp;Waiting&amp;nbsp;for&amp;nbsp;master&amp;nbsp;to&amp;nbsp;send&amp;nbsp;event\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_Host:&amp;nbsp;127.0.0.1\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_User:&amp;nbsp;rep\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Master_Port:&amp;nbsp;3307\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp