INSERT INTO `es_article` (`article_id`, `user_id`, `cat_id`, `type`, `title`, `content`, `click_count`, `is_show`, `is_public`, `add_time`) VALUES
	(88, 0, 6, 2, 'PHP扩展PDO', '&lt;p style=&quot;text-align: center;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 24px;&quot;&gt;PHP扩展PDO&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h3&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 18px;&quot;&gt;1.基本概念&lt;/span&gt;&lt;/strong&gt;&lt;/h3&gt;&lt;hr/&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;1、PDO：PHPDataObject的缩写。PDO扩展类库为PHP定义了一个轻量级的、一致性的接口，它提供了一个数据访问抽象层，这样无论使用什么数据库，都可以通过一致的函数执行查询和获取数据。PDO就是一个“数据库访问抽象层”，作用是统一各种数据库的访问接口。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;2、对任何数据库的操作，并不是使用PDO扩展本身执行的，必须针对不同的数据库服务器使用特定的&lt;span style=&quot;font-size: 16px; font-family: 宋体,SimSun;&quot;&gt;PDO驱动程序访问。如：MYSQL（PDO_MYSQL）。可以在phpinfo()函数中查看PDO部分的列表。&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;h3&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 24px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 18px;&quot;&gt;2.PDO的安装&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/h3&gt;&lt;hr/&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;1、Linux：安装PHP时，向configure命令中添加如下标志：&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 16px;&quot;&gt;--with-pdo-mysql=/usr/local/mysql//其中/usr/local/mysql为mysql安装目录&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 16px;&quot;&gt;2、Windows：&lt;br/&gt;在C:\\windows下找到php.ini文件&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 16px;&quot;&gt;（1）打开：extension=php_pdo.dll&lt;br/&gt;（2）打开：extension=php_pdo_mysql.dll&lt;/span&gt;&lt;/p&gt;&lt;h3&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;3.PDO与连接相关的选项&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/h3&gt;&lt;hr/&gt;&lt;table style=&quot;width: 1108px;&quot; width=&quot;1130&quot;&gt;&lt;caption style=&quot;border-color: rgb(216, 216, 216); text-align: center; vertical-align: middle;&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO的数据库连接选项&lt;/span&gt;&lt;br/&gt;&lt;/caption&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid; background-color: rgb(141, 179, 226);&quot; align=&quot;center&quot; valign=&quot;middle&quot; width=&quot;392&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;选项名&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid; background-color: rgb(141, 179, 226);&quot; align=&quot;center&quot; valign=&quot;middle&quot; width=&quot;737&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;描述&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_AUTOCOMMIT&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;确定PDO是否关闭自动提交功能，设置FALSE值时关闭&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_CASE&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;强制PDO获取的表字段字符的大小写转换，或原样使用列信息&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_ERRMODE&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;设置错误处理的模式&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_PERSISTENT&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;确定连接是否为持久连接，默认为FALSE&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_ORACLE_NULLS&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;将返回的空字符串转换为SQL的NULL&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_PREFETCH&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;设置应用程序提前获取的数据大小，以K字节为单位&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_TIMEOUT&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;设置超时之前等待的时间(秒数)&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_SERVER_INFO&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;包含与数据库特有的服务器信息&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_SERVER_VERSION&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;包含与数据库服务器版本号相关的信息&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_CLIENT_VERSION&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;包含与数据库客户端版本号相关的信息&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;392&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO::ATTR_CONNECTION_STATUS&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;737&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;包含数据库特有的与连接状态相关的信息&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;h3&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;4.PDO对象中的成员方法&lt;/span&gt;&lt;/strong&gt;&lt;/h3&gt;&lt;hr/&gt;&lt;h3&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/h3&gt;&lt;table width=&quot;1130&quot;&gt;&lt;caption style=&quot;border-color: rgb(216, 216, 216); text-align: center; vertical-align: middle;&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;PDO类中的成员方法(13个)&lt;/span&gt;&lt;br/&gt;&lt;/caption&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid; background-color: rgb(141, 179, 226);&quot; align=&quot;center&quot; valign=&quot;middle&quot; width=&quot;374&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;方法名&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid; background-color: rgb(141, 179, 226);&quot; align=&quot;center&quot; valign=&quot;middle&quot; width=&quot;755&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;描述&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;getAttribute()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;获取一个&amp;quot;数据库对象连接&amp;quot;的属性&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;setAttribute()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;为一个&amp;quot;数据库连接对象&amp;quot;设定属性&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;errorCode()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;获取错误码&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;errorInfo()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;获取错误的信息&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;exec()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;处理一条SQL语句，并返回所影响的条目数&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;query()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;处理一条SQL语句，并返回一个&amp;quot;PDOStatement&amp;quot;对象&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;quote()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;为某个SQL中的字符串添加引号&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;lastInsertId()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;获取插入到表中的最后一条数据的主键值&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;prepare()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;负责准备要执行SQL语句&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;getAvailableDrivers()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;获取有效的PDO驱动器名称&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;beginTransaction()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;开始一个事务，表明回滚起始点&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;commit()&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;提交一个事务，并执行SQL&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;374&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;rollback()&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all; border-color: rgb(216, 216, 216); border-width: 1px; border-style: solid;&quot; rowspan=&quot;1&quot; colspan=&quot;1&quot; valign=&quot;top&quot; width=&quot;755&quot;&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;回滚一个事务&lt;br/&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;h3&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;5.PDO实践操作&lt;/span&gt;&lt;/strong&gt;&lt;/h3&gt;&lt;hr/&gt;&lt;h4&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;5.1连接数据库&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/h4&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\n\n/*=======连接数据库========*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr=array(PDO::ATTR_AUTOCOMMIT=&amp;gt;0);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo=new&amp;nbsp;PDO(&amp;quot;mysql:host=localhost;dbname=oop&amp;quot;,&amp;quot;root&amp;quot;,&amp;quot;111111&amp;quot;,$arr);//创建PDO对象\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//其中$dsn(数据源名):&amp;quot;mysqli:host=localhost;dbname=oop&amp;quot;&amp;nbsp;=&amp;gt;用来定义一个确定的数据库和必须用到的驱动程序\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}catch(PDOException&amp;nbsp;$e){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;数据库连接失败:&amp;quot;.$e-&amp;gt;getMessage();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n/*=======调整PDO的行为属性========*/\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//setAttribute()&amp;nbsp;设置属性值的方法\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo-&amp;gt;setAttribute(PDO::ATTR_ERRMODE,PDO::ERRMODE_EXCEPTION);\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//getAttribute()&amp;nbsp;得到属性值的方法\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$pdo-&amp;gt;getAttribute(PDO::ATTR_AUTOCOMMIT).&amp;quot;&amp;lt;br&amp;gt;&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$pdo-&amp;gt;getAttribute(PDO::ATTR_ERRMODE).&amp;quot;&amp;lt;br&amp;gt;&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$pdo-&amp;gt;getAttribute(PDO::ATTR_SERVER_INFO).&amp;quot;&amp;lt;br&amp;gt;&amp;quot;;&lt;/pre&gt;&lt;h4&gt;&lt;span style=&quot;font-family: 宋体,SimSun; font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体,SimSun;&quot;&gt;5.2预处理&lt;/span&gt;&lt;/strong&gt;&lt;/h4&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\n/*=======连接数据库========*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo=new&amp;nbsp;PDO(&amp;quot;mysql:host=localhost;dbname=oop&amp;quot;,&amp;quot;root&amp;quot;,&amp;quot;111111&amp;quot;);//创建PDO对象\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo-&amp;gt;setAttribute(PDO::ATTR_ERRMODE,PDO::ERRMODE_EXCEPTION);//&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;设置错误模式为异常模式\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}catch(PDOException&amp;nbsp;$e){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;数据库连接失败:&amp;quot;.$e-&amp;gt;getMessage();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n\n/*=======使用预处理类PDOStatement向数据库中插入数据========*/\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*PDO中有两种使用占位符的语法:命名参数和问号参数\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*命名参数：关联数组\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*问号参数：索引数组\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第一步：准备好SQL语句======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$stmt=$pdo-&amp;gt;prepare(&amp;quot;insert&amp;nbsp;into&amp;nbsp;user(name,age,sex,email)&amp;nbsp;values(?,?,?,?)&amp;quot;);//问号参数\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt=$pdo-&amp;gt;prepare(&amp;quot;insert&amp;nbsp;into&amp;nbsp;user(name,age,sex,email)&amp;nbsp;values(:name,:age,:sex,:email)&amp;quot;);//命名参数\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第二步：绑定参数======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$stmt-&amp;gt;bindParam(1,$name,PDO::PARAM_STR);//第三个参数是设置变量类型的，可不写，php会自动转换\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$stmt-&amp;gt;bindParam(2,$age);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$stmt-&amp;gt;bindParam(3,$sex);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$stmt-&amp;gt;bindParam(4,$email);\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;bindParam(&amp;quot;:name&amp;quot;,$name,PDO::PARAM_STR);//第三个参数是设置变量类型的，可不写，php会自动转换\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;bindParam(&amp;quot;:age&amp;quot;,$age);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;bindParam(&amp;quot;:sex&amp;quot;,$sex);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;bindParam(&amp;quot;:email&amp;quot;,$email);\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第三步：给变量赋值======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$name=&amp;quot;qianlin&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$age=25;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$sex=&amp;quot;1&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$email=&amp;quot;zql_0539@163.com&amp;quot;;\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第四步：执行SQL(insert)语句======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;execute();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($stmt-&amp;gt;rowCount()){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;添加成功!&amp;lt;br&amp;gt;&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;影响的行数:&amp;quot;.$stmt-&amp;gt;rowCount().&amp;quot;&amp;lt;br&amp;gt;&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}catch(PDOException&amp;nbsp;$e){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$e-&amp;gt;getMessage();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\n/*=======连接数据库========*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo=new&amp;nbsp;PDO(&amp;quot;mysql:host=localhost;dbname=oop&amp;quot;,&amp;quot;root&amp;quot;,&amp;quot;111111&amp;quot;);//创建PDO对象\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo-&amp;gt;setAttribute(PDO::ATTR_ERRMODE,PDO::ERRMODE_EXCEPTION);//&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;设置错误模式为异常模式\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}catch(PDOException&amp;nbsp;$e){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;数据库连接失败:&amp;quot;.$e-&amp;gt;getMessage();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n\n/*=======使用预处理类PDOStatement向数据库中插入数据========*/\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*PDO中有两种使用占位符的语法:命名参数和问号参数\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*命名参数：关联数组\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*问号参数：索引数组\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第一步：准备好SQL语句======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$stmt=$pdo-&amp;gt;prepare(&amp;quot;insert&amp;nbsp;into&amp;nbsp;user(name,age,sex,email)&amp;nbsp;values(?,?,?,?)&amp;quot;);//问号参数\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt=$pdo-&amp;gt;prepare(&amp;quot;insert&amp;nbsp;into&amp;nbsp;user(name,age,sex,email)&amp;nbsp;values(:name,:age,:sex,:email)&amp;quot;);//命名参数\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第二步：执行SQL(insert)语句======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;execute($_GET);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($stmt-&amp;gt;rowCount()){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;添加成功!&amp;lt;br&amp;gt;&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;影响的行数:&amp;quot;.$stmt-&amp;gt;rowCount().&amp;quot;&amp;lt;br&amp;gt;&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}catch(PDOException&amp;nbsp;$e){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$e-&amp;gt;getMessage();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\n/*=======连接数据库========*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo=new&amp;nbsp;PDO(&amp;quot;mysql:host=localhost;dbname=oop&amp;quot;,&amp;quot;root&amp;quot;,&amp;quot;111111&amp;quot;);//创建PDO对象\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pdo-&amp;gt;setAttribute(PDO::ATTR_ERRMODE,PDO::ERRMODE_EXCEPTION);//&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;设置错误模式为异常模式\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}catch(PDOException&amp;nbsp;$e){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;数据库连接失败:&amp;quot;.$e-&amp;gt;getMessage();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n\n/*=======使用预处理类PDOStatement向数据库中插入数据========*/\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*PDO中有两种使用占位符的语法:命名参数和问号参数\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*命名参数：关联数组\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*问号参数：索引数组\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第一步：准备好SQL语句======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$stmt=$pdo-&amp;gt;prepare(&amp;quot;insert&amp;nbsp;into&amp;nbsp;user(name,age,sex,email)&amp;nbsp;values(?,?,?,?)&amp;quot;);//问号参数\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt=$pdo-&amp;gt;prepare(&amp;quot;insert&amp;nbsp;into&amp;nbsp;user(name,age,sex,email)&amp;nbsp;values(:name,:age,:sex,:email)&amp;quot;);//命名参数\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*=====第二步：执行SQL(insert)语句======*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*&amp;nbsp;Execute&amp;nbsp;a&amp;nbsp;prepared&amp;nbsp;statement&amp;nbsp;with&amp;nbsp;an&amp;nbsp;array&amp;nbsp;of&amp;nbsp;insert&amp;nbsp;values&amp;nbsp;(?&amp;nbsp;parameters)*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;execute(array(&amp;quot;qianlin3&amp;quot;,25,&amp;quot;1&amp;quot;,&amp;quot;zql@ss.com&amp;quot;));\n\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*&amp;nbsp;Execute&amp;nbsp;a&amp;nbsp;prepared&amp;nbsp;statement&amp;nbsp;with&amp;nbsp;an&amp;nbsp;array&amp;nbsp;of&amp;nbsp;insert&amp;nbsp;values&amp;nbsp;(named&amp;nbsp;parameters)*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$stmt-&amp;gt;execute(array(&amp;quot;:name&amp;quot;=&amp;gt;&amp;quot;qianlin1&amp;quot;,&amp;quot;age&amp;quot;=&amp;gt;25,&amp;quot;sex&amp;quot;=&amp;gt;&amp;quot;1&amp;quot;,&amp;quot;email&amp;quot;=&amp;gt;&amp;quot;aa@bb.com&amp;quot;));\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp